// Renaissance papermaker's brass wire watermark mould (c. 1550)
// Typical mould size: 4030 cm, adapted here to 8060 mm
// Features laid wires, chain wires, and a hand-laid watermark design

$fn=32;

// Overall dimensions scaled for 3D printing
mould_width = 80;
mould_height = 60;
mould_thickness = 3;
wire_diameter = 0.6;
laid_wire_spacing = 1.2;
chain_wire_spacing = 6;

module watermark_mould() {
    color("goldenrod") {
        // Main wooden frame
        difference() {
            cube([mould_width, mould_height, mould_thickness], center=true);

            // Hollow out center for wire area
            translate([0,0,-mould_thickness/2+0.5])
                cube([mould_width-6, mould_height-6, mould_thickness+1], center=true);
        }

        // Frame edges
        translate([0,0,mould_thickness/2]) {
            // Long sides
            for(side=[-1,1]) {
                translate([0, side*(mould_height/2-1.5), 0])
                    cube([mould_width-2, 3, 3], center=true);
            }

            // Short sides
            for(side=[-1,1]) {
                translate([side*(mould_width/2-1.5), 0, 0])
                    cube([3, mould_height-2, 3], center=true);
            }
        }
    }

    // Chain wires (thicker horizontal wires)
    color("silver") {
        for(y=[-mould_height/2+5 : chain_wire_spacing : mould_height/2-5]) {
            translate([0, y, 0])
                cube([mould_width-6, wire_diameter*1.5, wire_diameter], center=true);
        }

        // Laid wires (thin vertical wires)
        for(x=[-mould_width/2+5 : laid_wire_spacing : mould_width/2-5]) {
            translate([x, 0, 0])
                cube([wire_diameter, mould_height-6, wire_diameter], center=true);
        }
    }

    // Central watermark design - Florentine lily
    color("silver") translate([0,0,wire_diameter/2]) {
        // Main stem
        translate([0,-8,0]) 
            cylinder(h=wire_diameter, r1=1, r2=1);

        // Lily petals
        for(angle=[0,120,240]) {
            rotate([0,0,angle]) 
                translate([0,4,0]) 
                    cylinder(h=wire_diameter, r1=2.5, r2=0.5);
        }

        // Crown above lily
        translate([0,10,0]) {
            for(i=[-1,0,1]) {
                translate([i*2,0,0]) 
                    cylinder(h=wire_diameter, r1=1, r2=1);
            }
        }

        // Connecting wires
        color("silver") {
            translate([0,1,0]) 
                cube([0.6, 10, wire_diameter], center=true);
            translate([0,-5,0]) 
                cube([3, 0.6, wire_diameter], center=true);
        }
    }

    // Corner reinforcements
    color("goldenrod") {
        for(x=[-1,1]) {
            for(y=[-1,1]) {
                translate([x*(mould_width/2-3), y*(mould_height/2-3), 0])
                    cylinder(h=mould_thickness*1.5, r=3, center=true);
            }
        }
    }

    // Maker's mark on frame
    color("silver") translate([mould_width/2-2, 0, mould_thickness/2+1]) {
        rotate([90,0,0])
            linear_extrude(height=0.5) {
                text("FLORENCE 1552", size=2, halign="center");
            }
    }
}

