// Laidline historical artifact - Roman wax tablet with laid paper texture
// Realistic style, medium complexity, 3D printable

// Overall dimensions
tablet_width = 80;
tablet_height = 60;
tablet_thickness = 8;
wall_thickness = 3;

// Wood texture parameters
groove_width = 0.8;
groove_depth = 0.5;
groove_spacing = 2.5;

// Wax recess parameters
wax_depth = 4;
wax_margin = 5;

// Hinge parameters
hinge_radius = 3;
hinge_length = 10;
hinge_gap = 1;

// Laid line paper texture parameters
laid_line_spacing = 0.8;
laid_line_width = 0.2;
laid_line_depth = 0.1;
chain_line_spacing = 10;
chain_line_width = 0.4;

module wood_grooves(width, height, depth) {
    for (x = [groove_spacing/2 : groove_spacing : width]) {
        translate([x, 0, -depth/2])
            cube([groove_width, height, depth], center = true);
    }
}

module laid_texture(width, height) {
    // Laid lines (horizontal)
    for (y = [laid_line_spacing/2 : laid_line_spacing : height]) {
        translate([width/2, y, -laid_line_depth/2])
            cube([width, laid_line_width, laid_line_depth], center = true);
    }

    // Chain lines (vertical)
    for (x = [chain_line_spacing/2 : chain_line_spacing : width]) {
        translate([x, height/2, -laid_line_depth/2])
            cube([chain_line_width, height, laid_line_depth], center = true);
    }
}

module wax_tablet() {
    difference() {
        // Main tablet body
        union() {
            // Outer shell
            color("saddlebrown")
            cube([tablet_width, tablet_height, tablet_thickness], center = true);

            // Wood grain texture on back
            translate([0, 0, tablet_thickness/2 - groove_depth/2])
                wood_grooves(tablet_width, tablet_height, groove_depth);
        }

        // Wax recess
        translate([0, 0, tablet_thickness/2 - wax_depth/2])
            cube([tablet_width - 2*wax_margin, tablet_height - 2*wax_margin, wax_depth], center = true);

        // Hinge holes
        translate([tablet_width/2 - hinge_gap - hinge_radius, tablet_height/2 - hinge_length/2, 0])
            rotate([90, 0, 0])
            cylinder(h = tablet_thickness, r = hinge_radius, center = true);

        translate([tablet_width/2 - hinge_gap - hinge_radius, -tablet_height/2 + hinge_length/2, 0])
            rotate([90, 0, 0])
            cylinder(h = tablet_thickness, r = hinge_radius, center = true);
    }

    // Wax layer with laid paper texture
    translate([0, 0, tablet_thickness/2 - wax_depth + 0.5])
    color("darkgoldenrod")
    difference() {
        cube([tablet_width - 2*wax_margin - 1, tablet_height - 2*wax_margin - 1, wax_depth - 1], center = true);

        // Laid paper texture on wax
        laid_texture(tablet_width - 2*wax_margin - 2, tablet_height - 2*wax_margin - 2);
    }

    // Reinforcement corners
    corner_size = 5;
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x*(tablet_width/2 - corner_size/2), y*(tablet_height/2 - corner_size/2), 0])
            color("saddlebrown")
            difference() {
                cube([corner_size, corner_size, tablet_thickness], center = true);
                cylinder(h = tablet_thickness + 1, r = corner_size/2, center = true);
            }
        }
    }
}

