        // Central wheel disc
        difference() {
            cylinder(h=8, d=wheel_diameter, center=true);

            // Central hole for handle
            cylinder(h=10, d=handle_diameter, center=true);

            // Decorative inner ring
            cylinder(h=9, d=wheel_diameter-8, center=true);
        }

        // Reinforcement ring around edge
        translate([0, 0, 4.5]) 
            cylinder(h=2, d=wheel_diameter+2, center=true);
    }
}

// Pricking teeth around wheel edge
