    color("silver") {
        for(i = [0:15:359]) {
            rotate([0, 0, i])
            translate([wheel_diameter/2, 0, 0])
            rotate([90, 0, 0])
            cylinder(h=prick_length, d1=2, d2=0.5, center=false);
        }
    }
}

// Wooden handle with grip rings
