    color("saddlebrown") {
        // Main handle shaft
        translate([-handle_length/2, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=handle_length, d=handle_diameter, center=true);

        // Grip rings
        for(pos = [-30, -15, 0, 15, 30]) {
            translate([pos, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=4, d=handle_diameter+2, center=true);
        }

        // End cap
        translate([handle_length/2, 0, 0])
        rotate([0, 90, 0])
        sphere(d=handle_diameter+2);
    }
