    color("goldenrod") {
        // Cross arms
        for(angle = [0:90:270]) {
            rotate([0, 0, angle])
            translate([wheel_diameter/4, 0, 4.5])
            cube([wheel_diameter/2-5, 3, 1], center=true);
        }

        // Central decorative boss
        translate([0, 0, 5])
        cylinder(h=2, d=6, center=true);

        // Small rivet details
        for(angle = [45:90:315]) {
            rotate([0, 0, angle])
            translate([wheel_diameter/3, 0, 5])
            cylinder(h=1, d=3, center=true);
        }
    }
}

// Assembly
