// Egyptian Papyrus Frame inspired by New Kingdom cartouches (1550-1070 BCE)
// Features lotus motifs and hieroglyphic border typical of temple and tomb art

$fn = 64; // Smooth curves for realistic appearance

// Main frame dimensions - sized for 3D printing
frame_width = 60;
frame_height = 70;
frame_thickness = 8;
border_width = 6;

// Colors for realistic appearance
frame_color = "BurlyWood";
accent_color = "DarkGoldenrod";

// Main frame base
module frame_base() {
    color(frame_color)
    difference() {
        // Outer frame
        cube([frame_width, frame_height, frame_thickness], center = true);

        // Inner opening for papyrus
        translate([0, 0, frame_thickness/2 - 1])
        cube([frame_width - 2*border_width, frame_height - 2*border_width, frame_thickness], center = true);
    }
}

// Lotus flower motif - sacred to ancient Egyptians
module lotus_motif() {
    color(accent_color)
    union() {
        // Center of lotus
        translate([0, 0, frame_thickness/2 + 0.1])
        cylinder(h = 1, r = 2);

        // Petals around center
        for (i = [0:5]) {
            rotate([0, 0, i*60])
            translate([0, 3, frame_thickness/2 + 0.1])
            scale([1, 1.5, 1])
            sphere(r = 1.5);
        }
    }
}

// Hieroglyphic border pattern
module hieroglyph_border() {
    color(accent_color)
    // Top border
    for (i = [-frame_width/2 + border_width : 5 : frame_width/2 - border_width]) {
        translate([i, frame_height/2 - border_width/2, frame_thickness/2])
        cube([2, 2, 1]);
    }

    // Bottom border
    for (i = [-frame_width/2 + border_width : 5 : frame_width/2 - border_width]) {
        translate([i, -frame_height/2 + border_width/2, frame_thickness/2])
        cube([2, 2, 1]);
    }
}

// Corner decorations - ankh symbols simplified
module corner_ankh() {
    color(accent_color)
    union() {
        // Vertical stem
        translate([0, 0, frame_thickness/2 + 0.1])
        cube([1, 4, 1]);

        // Horizontal bar
        translate([-1.5, 1.5, frame_thickness/2 + 0.1])
        cube([4, 1, 1]);

        // Top loop
        translate([0, 3.5, frame_thickness/2 + 0.1])
        cylinder(h = 1, r = 2);
    }
}

// Back mounting pegs for 3D printing stability
module mounting_pegs() {
    color(frame_color)
    for (x = [-frame_width/3, frame_width/3]) {
        for (y = [-frame_height/3, frame_height/3]) {
            translate([x, y, -frame_thickness/2 - 1])
            cylinder(h = 2, r = 2);
        }
    }
}

// Assembly
union() {
    // Main frame structure
    frame_base();

    // Corner decorations
    translate([frame_width/2 - border_width/2, frame_height/2 - border_width/2, 0])
    rotate([0, 0, 0])
    corner_ankh();

    translate([-frame_width/2 + border_width/2, frame_height/2 - border_width/2, 0])
    rotate([0, 0, 90])
    corner_ankh();

    translate([frame_width/2 - border_width/2, -frame_height/2 + border_width/2, 0])
    rotate([0, 0, -90])
    corner_ankh();

    translate([-frame_width/2 + border_width/2, -frame_height/2 + border_width/2, 0])
    rotate([0, 0, 180])
    corner_ankh();

    // Lotus motifs on sides
    for (y = [-frame_height/2 + 10 : 15 : frame_height/2 - 10]) {
        translate([frame_width/2 - border_width/2, y, 0])
        lotus_motif();

        translate([-frame_width/2 + border_width/2, y, 0])
        lotus_motif();
    }

    // Hieroglyphic border
    hieroglyph_border();

    // Mounting pegs for 3D printing
    mounting_pegs();
}

