// Realistic Plate - Medium Complexity
// Diameter: 80mm, Height: 8mm
// Features: Rim, base foot, subtle curvature, drainage channels

$fn = 50;  // Smooth curves

// Main parameters
plate_diameter = 80;
plate_height = 8;
rim_height = 3;
rim_thickness = 2;
base_foot_height = 2;
wall_thickness = 1.5;

// Curvature parameters
center_depth = 1.2;
rim_curve = 0.8;

module plate_base() {
    // Main plate body with gentle curvature
    difference() {
        // Outer shape - shallow dome
        union() {
            // Base cylinder
            cylinder(h=plate_height - rim_height, d=plate_diameter);

            // Rim
            translate([0, 0, plate_height - rim_height])
                cylinder(h=rim_height, d=plate_diameter);
        }

        // Hollow center with curved bottom
        translate([0, 0, wall_thickness])
            cylinder(h=plate_height - wall_thickness, d=plate_diameter - 2*wall_thickness);

        // Curved bottom
        translate([0, 0, wall_thickness - center_depth])
            sphere(d=plate_diameter - 2*wall_thickness);
    }
}

module rim_detail() {
    // Create rim curvature
    difference() {
        // Top rim
        translate([0, 0, plate_height - rim_height])
            cylinder(h=rim_height, d=plate_diameter);

        // Inner bevel
        translate([0, 0, plate_height - rim_height - 0.5])
            cylinder(h=rim_height + 1, d=plate_diameter - 2*rim_thickness);
    }
}

module base_foot() {
    // Circular foot for stability
    foot_diameter = plate_diameter - 10;

    difference() {
        // Main foot
        cylinder(h=base_foot_height, d=foot_diameter);

        // Hollow center
        cylinder(h=base_foot_height + 0.1, d=foot_diameter - 4);
    }
}

module drainage_grooves() {
    // Subtle grooves for water drainage
    groove_width = 1;
    groove_depth = 0.3;
    num_grooves = 8;

    for (i = [0:num_grooves-1]) {
        angle = i * 360 / num_grooves;

        rotate([0, 0, angle])
            translate([plate_diameter/2 - 10, -groove_width/2, wall_thickness])
                cube([20, groove_width, groove_depth]);
    }
}

// Assemble the plate
union() {
    // Main plate
    plate_base();

    // Rim detail for realistic curvature
    rim_detail();

    // Base foot
    translate([0, 0, 0])
        base_foot();

    // Drainage grooves
    drainage_grooves();
}

// Smooth bottom transition
translate([0, 0, base_foot_height])
    difference() {
        cylinder(h=0.5, d=plate_diameter);
        cylinder(h=0.6, d=plate_diameter - 2);
    }
