// Realistic Medium-Complexity Blender Kitchen Appliance
// Designed for 3D printing: no overhangs, 2 mm walls, ≈120 mm tall

$fn = 36;  // Smooth curves
wall = 2;  // 3D-printable wall thickness

// Main assembly
module blender() {
    difference() {
        // Base housing
        base();

        // Motor shaft hole
        translate([0, 0, 25])
            cylinder(h = 30, d = 8);
    }

    // Buttons panel
    translate([18, -25, 0])
        buttons_panel();

    // Jar assembly
    translate([0, 0, 30])
        jar();

    // Lid
    translate([0, 0, 30 + 55])
        lid();
}

// Base housing
module base() {
    // Outer shell
    difference() {
        union() {
            // Main body
            cylinder(h = 30, d = 60);

            // Back bump for cord
            translate([0, 25, 10])
                scale([1, 0.6, 0.5])
                    sphere(d = 25);
        }

        // Hollow interior
        cylinder(h = 28, d = 60 - 2 * wall);
    }

    // Feet
    for (a = [0, 120, 240])
        rotate([0, 0, a])
            translate([23, 0, -2])
                cylinder(h = 4, d = 8);
}

// Buttons and dial panel
module buttons_panel() {
    // Panel plate
    cube([35, 15, 8], center = true);

    // Three push buttons
    for (i = [0, 1, 2])
        translate([10 - i * 10, 0, 5])
            cylinder(h = 6, d = 6);

    // Speed dial
    translate([10, 0, 8])
        cylinder(h = 6, d = 12);

    // Dial grip ridges
    for (a = [0:30:330])
        rotate([0, 0, a])
            translate([5, 0, 8 + 3])
                cube([2, 1, 4], center = true);
}

// Glass/plastic jar
module jar() {
    // Jar walls
    difference() {
        // Outer
        cylinder(h = 55, d1 = 40, d2 = 45);

        // Inner cavity
        translate([0, 0, -1])
            cylinder(h = 57, d1 = 40 - 2 * wall, d2 = 45 - 2 * wall);
    }

    // Handle
    difference() {
        // Handle grip
        translate([25, 0, 25])
            rotate([0, 90, 0])
                torus(ri = 6, ro = 10);

        // Cut hollow
        translate([25, 0, 25])
            rotate([0, 90, 0])
                torus(ri = 3, ro = 8);
    }

    // Spout
    translate([0, 22.5, 50])
        rotate([90, 0, 0])
            cylinder(h = 8, d1 = 15, d2 = 5);
}

// Lid
module lid() {
    // Top
    difference() {
        // Outer lid
        cylinder(h = 5, d = 47);

        // Inner plug
        cylinder(h = 7, d = 41);
    }

    // Center cap
    translate([0, 0, 5])
        cylinder(h = 8, d = 20);

    // Cap grip
    for (a = [0:45:315])
        rotate([0, 0, a])
            translate([10, 0, 9])
                cube([8, 2, 4], center = true);
}

// Torus helper for handle
module torus(ri, ro) {
    rotate_extrude()
        translate([ro, 0, 0])
            circle(r = ri);
}

// Render
blender();

// Realistic 2-slice toaster, medium complexity
// Dimensions in mm, printable with 2 mm walls

$fn = 50;

// Overall size
toaster_w = 110;
toaster_d = 80;
toaster_h = 70;

// Wall thickness
wall = 2;

module toaster_body() {
    difference() {
        // Outer shell
        union() {
            // Main block
            translate([0, 0, toaster_h/2])
                rounded_box(toaster_w, toaster_d, toaster_h, 5);
            // Feet
            for(x=[-1,1])
            for(y=[-1,1])
                translate([x*(toaster_w/2 - 10), y*(toaster_d/2 - 10), 3])
                    cylinder(h=6, r=5);
        }
        // Slot openings
        slot_w = 30;
        slot_d = toaster_d - 20;
        slot_h = 5;
        for(x=[-1,1])
            translate([x*toaster_w/4, 0, toaster_h - slot_h/2])
                cube([slot_w, slot_d, slot_h + 1], center=true);
        // Inner cavity
        translate([0, 0, toaster_h/2])
            rounded_box(toaster_w - 2*wall, toaster_d - 2*wall, toaster_h - wall, 3);
    }
}

module rounded_box(w, d, h, r) {
    hull() {
        for(x=[-1,1])
        for(y=[-1,1])
        for(z=[-1,1])
            translate([x*(w/2 - r), y*(d/2 - r), z*(h/2 - r)])
                sphere(r=r);
    }
}

module control_panel() {
    // Front face plate
    translate([toaster_w/2, 0, 25])
        rotate([0, 90, 0])
        difference() {
            rounded_box(45, 20, 4, 4);
            // Knob hole
            translate([0, 0, 0])
                cylinder(h=5, r=6);
        }
    // Knob
    translate([toaster_w/2 + 2, 0, 25])
        rotate([0, 90, 0])
        cylinder(h=8, r=5);
    // Lever
    translate([toaster_w/2 - 5, 0, 15])
        rotate([0, 0, 90])
        union() {
            cube([30, 5, 5]);
            translate([15, 0, 2.5])
                sphere(r=4);
        }
    // Buttons
    for(i=[0,1]) {
        translate([toaster_w/2 - 10, 15 - i*30, 35])
            difference() {
                cube([8, 8, 2], center=true);
                translate([0, 0, 1])
                    sphere(r=3);
            }
    }
}

module crumb_tray() {
    // Tray slot
    translate([0, toaster_d/2 - 1, 8])
        rotate([90, 0, 0])
        difference() {
            cube([toaster_w - 30, 10, 2], center=true);
            translate([0, 0, -0.5])
                cube([toaster_w - 34, 6, 3], center=true);
        }
    // Handle
    translate([0, toaster_d/2 + 4, 8])
        cube([10, 8, 3], center=true);
}

// Assembly
toaster_body();
control_panel();
crumb_tray();

// Medium complexity realistic microwave
// Overall dimensions: 100mm(W) x 75mm(D) x 60mm(H)

$fn = 32;

// Main body shell
module body_shell() {
    difference() {
        // Outer shell
        cube([100, 75, 60], center = false);

        // Hollow interior - 2mm walls
        translate([2, 2, 2])
            cube([96, 71, 56], center = false);
    }
}

// Door
module door() {
    // Door frame
    translate([-0.5, 10, 5])
        cube([2, 55, 50]);

    // Door window
    translate([0, 12, 7])
        difference() {
            cube([1, 51, 46]);
            translate([0.5, 3, 3])
                cube([2, 45, 40]);
        }

    // Door handle
    translate([-2, 50, 30])
        rotate([0, 90, 0])
            cylinder(h = 25, d1 = 5, d2 = 5);
}

// Control panel
module control_panel() {
    // Panel backing
    translate([100, 20, 45])
        cube([5, 35, 15]);

    // Display
    translate([105, 25, 55])
        cube([1, 20, 5]);

    // Buttons
    for(y = [0:6:24]) {
        translate([105, 22 + y, 48])
            cylinder(h = 3, d = 5);
    }

    // Dial
    translate([105, 45, 52])
        rotate([90, 0, 0])
            cylinder(h = 3, d = 8);
}

// Feet
module feet() {
    // Four feet at corners
    positions = [[5, 5], [5, 70], [95, 5], [95, 70]];
    for(pos = positions) {
        translate([pos[0], pos[1], -3])
            cylinder(h = 3, d = 5);
    }
}

// Ventilation grilles
module vents() {
    // Side vents
    translate([50, 75, 30])
        rotate([0, 90, 0])
            for(i = [0:4:20]) {
                translate([i, 0, 0])
                    cube([2, 2, 75]);
            }

    // Back vents
    translate([100, 50, 30])
        rotate([0, 0, 90])
            for(i = [0:4:20]) {
                translate([i, 0, 0])
                    cube([2, 2, 50]);
            }
}

// Power cord
module power_cord() {
    translate([50, 75, 10])
        rotate([90, 0, 0])
            cylinder(h = 30, d = 3);
}

// Assembly
union() {
    body_shell();
    door();
    control_panel();
    feet();
    vents();
    power_cord();
}

// Coffee Maker 3D Model - Realistic Style, Medium Complexity
// Overall dimensions: 100mm(W) x 120mm(D) x 140mm(H)

$fn = 32;
wall_thickness = 2;

module main_body() {
    // Main housing
    difference() {
        // Outer shell
        cube([90, 110, 100], center=true);

        // Hollow interior
        translate([0, 0, wall_thickness])
            cube([86, 106, 96], center=true);
    }
}

module water_tank() {
    // Transparent water tank on back
    translate([0, -55, 30]) {
        difference() {
            // Tank body
            cube([40, 15, 60], center=true);

            // Hollow interior
            translate([0, 0, wall_thickness])
                cube([36, 11, 56], center=true);
        }

        // Tank handle
        translate([25, 0, 0])
            rotate([90, 0, 0])
                cylinder(h=30, r1=3, r2=3);
    }
}

module filter_basket() {
    // Drip filter basket
    translate([0, 20, -30]) {
        difference() {
            // Basket cone
            cylinder(h=25, r1=20, r2=15);

            // Hollow interior
            translate([0, 0, wall_thickness])
                cylinder(h=23, r1=18, r2=13);
        }

        // Basket handle
        translate([22, 0, 12])
            rotate([0, 90, 0])
                cylinder(h=15, r1=2, r2=2);
    }
}

module carafe() {
    // Glass carafe
    translate([0, 20, -70]) {
        difference() {
            // Carafe body
            cylinder(h=35, r1=18, r2=20);

            // Interior space
            translate([0, 0, wall_thickness])
                cylinder(h=33, r1=16, r2=18);
        }

        // Handle
        translate([20, 0, 17])
            rotate([0, 90, 0])
                cylinder(h=35, r1=3, r2=3);

        // Spout
        translate([0, -20, 30])
            rotate([0, 0, 0])
                cube([30, 5, 5], center=true);
    }
}

module control_panel() {
    // Front control panel
    translate([0, 55, 20]) {
        // Panel base
        cube([60, 5, 30], center=true);

        // Power button
        translate([-20, 3, 0])
            cylinder(h=3, r1=5, r2=5);

        // Brew strength dial
        translate([0, 3, 0])
            cylinder(h=3, r1=7, r2=7);

        // Timer button
        translate([20, 3, 0])
            cylinder(h=3, r1=5, r2=5);
    }
}

module heating_plate() {
    // Hot plate for carafe
    translate([0, 20, -50]) {
        difference() {
            cylinder(h=3, r1=25, r2=25);
            cylinder(h=3, r1=23, r2=23);
        }
    }
}

module base_feet() {
    // Four rubber feet
    for(x = [-35, 35])
        for(y = [-45, 45])
            translate([x, y, -52])
                cylinder(h=3, r1=4, r2=4);
}

module display_screen() {
    // LED display
    translate([0, 55, 35]) {
        difference() {
            cube([40, 2, 15], center=true);
            translate([0, 1, 0])
                cube([36, 2, 11], center=true);
        }
    }
}

// Assembly
union() {
    main_body();
    water_tank();
    filter_basket();
    carafe();
    control_panel();
    heating_plate();
    base_feet();
    display_screen();
}

// Realistic medium-complexity kitchen mixer
// Overall size ≈ 110 mm tall, 80 mm wide, 100 mm deep

$fn = 32;              // smooth curves
wall = 2;              // 3D-print friendly wall thickness

// ----- Base housing -------------------------------------------------
module base() {
    difference() {
        // main body
        translate([0,0,10])
            cube([70,80,20], center=true);

        // front sloped face
        translate([0,-41,20])
            rotate([45,0,0])
                cube([68,30,30], center=true);

        // rear sloped face
        translate([0,41,20])
            rotate([-45,0,0])
                cube([68,30,30], center=true);

        // hollow inside
        translate([0,0,10])
            cube([66-wall*2,76-wall*2,18], center=true);
    }

    // rubber feet
    for(x=[-25,25])
        for(y=[-25,25])
            translate([x,y,0])
                cylinder(h=2, d1=6, d2=5);
}

// ----- Motor tower --------------------------------------------------
module tower() {
    union() {
        // vertical column
        cylinder(h=55, d1=30, d2=25);
        translate([0,0,55])
            cylinder(h=15, d1=25, d2=30);

        // control panel
        translate([0,-15,35])
            cube([40,10,20], center=true);
    }
}

// ----- Speed dial ---------------------------------------------------
module dial() {
    rotate([90,0,0])
        cylinder(h=8, d1=12, d2=10);
    for(i=[0:5:355])
        rotate([0,0,i])
            translate([5.5,0,-1])
                cube([1,1,10], center=true);
}

// ----- Mixer head & gearbox -----------------------------------------
module head() {
    difference() {
        union() {
            // gearbox box
            translate([0,0,70])
                cube([55,40,30], center=true);

            // curved transition
            translate([0,0,55])
                cylinder(h=15, d1=25, d2=50);
        }

        // hollow cavity
        translate([0,0,70])
            cube([51-wall*2,36-wall*2,26], center=true);
    }
}

// ----- Beaters ------------------------------------------------------
module beaters() {
    for(side=[-1,1])
        translate([side*18,0,85]) {
            // shaft
            cylinder(h=35, d=5);

            // wire loops
            for(z=[5,15,25]) {
                rotate([0,0,45])
                    translate([0,0,z])
                        scale([1,0.2,1])
                            rotate_extrude()
                                translate([5,0,0])
                                    circle(d=1.5);
            }
        }
}

// ----- Bowl ---------------------------------------------------------
module bowl() {
    difference() {
        // outer bowl
        translate([0,0,48])
            cylinder(h=40, d1=70, d2=75);

        // inner cavity
        translate([0,0,50])
            cylinder(h=38, d1=66, d2=71);
    }

    // handle
    translate([35,0,68])
        rotate([0,90,0])
            cylinder(h=35, d=8);
    translate([35,0,68])
        rotate([0,90,0])
            cylinder(h=30, d=4);
}

// ----- Buttons ------------------------------------------------------
module buttons() {
    // on/off button
    translate([0,-15,45])
        cube([10,5,5], center=true);

    // turbo button
    translate([0,-15,40])
        cube([8,5,5], center=true);
}

// ------------------- Assembly ---------------------------------------
base();
translate([0,0,20]) tower();
translate([0,-15,35]) dial();
head();
beaters();
bowl();
buttons();

// Electric Kettle - Realistic medium-complexity model
// Overall height ~110 mm, fits most FDM printers

// Parameters
wall = 2;           // wall thickness for 3D printability
base_r = 30;        // kettle body radius
body_h = 60;        // kettle body height
neck_r = 20;        // neck radius
spout_len = 25;     // spout protrusion
handle_clear = 15;  // finger clearance inside handle
button_w = 8;       // power button width

// Kettle body – swept cylinder with rounded top
module kettle_body() {
    difference() {
        union() {
            // Main cylinder
            cylinder(h = body_h, r1 = base_r, r2 = base_r, $fn = 64);
            // Rounded top
            translate([0, 0, body_h])
                scale([1, 1, 0.6])
                    sphere(r = base_r, $fn = 64);
        }
        // Hollow inside
        translate([0, 0, -1])
            cylinder(h = body_h + 2, r1 = base_r - wall, r2 = base_r - wall, $fn = 64);
        translate([0, 0, body_h])
            scale([1, 1, 0.6])
                sphere(r = base_r - wall, $fn = 64);
    }
}

// Spout
module spout() {
    difference() {
        union() {
            // Spout tube
            translate([0, base_r - 5, body_h - 10])
                rotate([90, 0, 0])
                    cylinder(h = spout_len, r1 = 6, r2 = 4);
            // Spout base blend
            translate([0, base_r - 5, body_h - 15])
                sphere(r = 7);
        }
        // Hollow spout
        translate([0, base_r - 5, body_h - 10])
            rotate([90, 0, 0])
                cylinder(h = spout_len + 2, r1 = 4, r2 = 2);
    }
}

// Handle
module handle() {
    difference() {
        // Outer handle loop
        translate([-base_r - 8, 0, body_h / 2])
            rotate([90, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 50, r1 = 5, r2 = 5);
        // Inner clearance
        translate([-base_r - 8, 0, body_h / 2])
            rotate([90, 0, 0])
                rotate([0, 90, 0])
                    cylinder(h = 50, r1 = 5 - wall, r2 = 5 - wall);
        // Top and bottom caps
        translate([-base_r - 8, 0, body_h / 2 + 25])
            rotate([90, 0, 0])
                cylinder(h = 10, r1 = 5, r2 = 5);
        translate([-base_r - 8, 0, body_h / 2 - 25])
            rotate([90, 0, 0])
                cylinder(h = 10, r1 = 5, r2 = 5);
    }
}

// Lid
module lid() {
    difference() {
        union() {
            // Lid disk
            cylinder(h = 3, r = neck_r, $fn = 64);
            // Lid knob
            translate([0, 0, 3])
                cylinder(h = 8, r1 = 6, r2 = 4);
        }
        // Hollow knob
        translate([0, 0, 3])
            cylinder(h = 9, r1 = 3, r2 = 2);
    }
}

// Power button
module power_button() {
    translate([base_r - 2, 0, body_h / 2])
        cube([button_w, 6, 4], center = true);
}

// Base plate with feet
module base() {
    difference() {
        // Base disc
        cylinder(h = wall, r = base_r + 4, $fn = 64);
        // Cable notch
        translate([base_r + 2, 0, -1])
            cube([6, 10, wall + 2], center = true);
    }
    // Feet
    for (a = [0:120:359])
        rotate([0, 0, a])
            translate([base_r - 8, 0, -2])
                cylinder(h = 2, r = 3);
}

// Assembly
base();
translate([0, 0, wall])
    kettle_body();
translate([0, 0, body_h + wall - 3])
    lid();
spout();
handle();
power_button();

// Manual can-opener, medium-complexity, 3D-printable
// Overall length ≈ 110 mm, all walls ≥ 2 mm

// ---- Parameters ----
handle_len  = 65;   // main gripping handle
handle_d    = 14;
crank_len   = 45;   // turning lever
crank_d     = 10;
head_w      = 30;   // opener head block
head_h      = 22;
head_t      = 12;
blade_h     = 8;
gear_teeth  = 12;
gear_h      = 6;
axle_d      = 4;
washer_t    = 1.5;
$fn = 36;

// ---- Helper module for rounded ends ----
module round_cylinder(h, d)
{
    cylinder(h=h-2*2, d=d);
    translate([0,0,h-2]) sphere(d=d);
    translate([0,0,0])   mirror([0,0,1]) sphere(d=d);
}

// ---- Main handle ----
module main_handle()
{
    translate([0,0,-handle_d/2])
        rotate([90,0,0])
            round_cylinder(handle_len, handle_d);
    // grip rings
    for(i=[1:4])
        translate([-handle_len/2 + i*13,0,0])
            scale([1,1.1,1])
                cylinder(h=2.5, d=handle_d+0.5, center=true);
}

// ---- Crank lever ----
module crank()
{
    translate([handle_len/2+crank_len/2,0,0])
        rotate([90,0,0])
            round_cylinder(crank_len, crank_d);
    // knob
    translate([handle_len/2+crank_len+crank_d/2,0,0])
        sphere(d=crank_d*1.3);
}

// ---- Rotating feed gear ----
module feed_gear()
{
    difference()
    {
        cylinder(h=gear_h, d=20);
        for(i=[0:gear_teeth-1])
            rotate([0,0,i*360/gear_teeth])
                translate([10-1.5,0,-1])
                    cube([3,2,gear_h+2], center=true);
    }
}

// ---- Cutting wheel ----
module cutting_wheel()
{
    difference()
    {
        union()
        {
            cylinder(h=blade_h, d=22);
            cylinder(h=blade_h*0.8, d=4); // axle stub
        }
        // sharp edge
        translate([0,0,blade_h/2])
            rotate([0,90,0])
                cylinder(h=24, d=1.2, $fn=6);
    }
}

// ---- Head block ----
module opener_head()
{
    difference()
    {
        // main solid block
        cube([head_w,head_t,head_h], center=true);
        // axle holes
        translate([head_w/2-8,0,head_h/2-gear_h/2])
            rotate([0,90,0])
                cylinder(h=head_t+2, d=axle_d+0.5, center=true);
        translate([head_w/2-8,0,-head_h/2+blade_h/2])
            rotate([0,90,0])
                cylinder(h=head_t+2, d=axle_d+0.5, center=true);
    }
    // add parts
    translate([head_w/2-8,0,head_h/2-gear_h/2])
        feed_gear();
    translate([head_w/2-8,0,-head_h/2+blade_h/2])
        cutting_wheel();
    // washers
    translate([head_w/2-8,head_t/2+washer_t/2,head_h/2-gear_h/2])
        cylinder(h=washer_t, d=axle_d+3);
    translate([head_w/2-8,-head_t/2-washer_t/2,head_h/2-gear_h/2])
        cylinder(h=washer_t, d=axle_d+3);
    translate([head_w/2-8,head_t/2+washer_t/2,-head_h/2+blade_h/2])
        cylinder(h=washer_t, d=axle_d+3);
    translate([head_w/2-8,-head_t/2-washer_t/2,-head_h/2+blade_h/2])
        cylinder(h=washer_t, d=axle_d+3);
}

// ---- Assembly ----
union()
{
    main_handle();
    crank();
    translate([-handle_len/2-head_w/2,0,handle_d/2-head_h/2])
        opener_head();
}

// Food Processor - Medium complexity realistic style

$fn = 24;

// Overall scale
scale_factor = 0.35;

// Main dimensions (mm)
base_width = 120 * scale_factor;
base_depth = 100 * scale_factor;
base_height = 45 * scale_factor;

motor_housing_width = 80 * scale_factor;
motor_housing_depth = 80 * scale_factor;
motor_housing_height = 90 * scale_factor;

bowl_diameter = 110 * scale_factor;
bowl_height = 80 * scale_factor;
bowl_wall = 2 * scale_factor;

lid_diameter = bowl_diameter;
lid_height = 15 * scale_factor;
feed_tube_diameter = 30 * scale_factor;
feed_tube_height = 40 * scale_factor;

// Base module
module base() {
    difference() {
        // Main base body
        translate([0, 0, base_height/2])
            cube([base_width, base_depth, base_height], center=true);

        // Bottom recess
        translate([0, 0, -2])
            cube([base_width - 4, base_depth - 4, base_height - 2], center=true);

        // Vent slots on sides
        for (i = [-1, 1]) {
