        translate([0, 0, base_height + motor_housing_height + bowl_height/2])
            cylinder(h=bowl_height, r=bowl_diameter/2, center=true);

        // Inner cavity
        translate([0, 0, base_height + motor_housing_height + bowl_wall])
            cylinder(h=bowl_height - bowl_wall*2, r=bowl_diameter/2 - bowl_wall, center=true);

        // Handle hole
        translate([bowl_diameter/2 - 5, 0, base_height + motor_housing_height + bowl_height/2])
            rotate([0, 90, 0])
                cylinder(h=20, r=8, center=true);
    }

