    difference() {
        cylinder(h = wall*2, r1 = pot_r + wall + 4, r2 = pot_r + wall + 6, $fn = 64);
        cylinder(h = wall*2, r = pot_r + wall, $fn = 64);
    }
}

// Handles on sides
module side_handles() {
    // Left handle
    translate([-(pot_r + wall + 15), 0, pot_h/2])
        rotate([0, 90, 0])
            handle_shape();
    // Right handle
    translate([(pot_r + wall + 15), 0, pot_h/2])
        rotate([0, 90, 0])
            mirror([1,0,0])
                handle_shape();
}

module handle_shape() {
    difference() {
        // Main grip
