            // Lid rim
            difference() {
                cylinder(h = wall, r = pot_r + wall - 1, $fn = 64);
                cylinder(h = wall + 1, r = pot_r, $fn = 64);
            }
            // Handle on top
            translate([0, 0, wall*2 + 5])
                lid_handle();
            // Steam release valve
            translate([pot_r/2, 0, wall*2])
                steam_valve();
            // Locking tabs (4 tabs)
            for (i = [0:3]) {
                rotate([0, 0, i*90])
                    translate([pot_r + wall - 3, 0, wall])
                        cube([6, 8, wall], center = true);
            }
        }
}

module lid_handle() {
    difference() {
        cylinder(h = 15, r = 8, $fn = 20);
        cylinder(h = 16, r = 4, $fn = 20);
    }
}

