    difference(){
        union(){
            // outer bowl
            rounded_cylinder(base_h, base_r, base_r*0.35);
            // rim lip
            translate([0, 0, base_h])
                cylinder(h=wall*1.5, r1=base_r+wall, r2=base_r+wall, $fn=64);
        }
        // hollow inside
        translate([0, 0, -0.1])
            rounded_cylinder(base_h+0.2, base_r-wall, base_r*0.35-wall);
    }
}

// Handle
