// Vegetable Steamer - Realistic Style, Medium Complexity

// Overall dimensions (mm)
steamer_height = 100;
base_height = 25;
lid_height = 20;
body_diameter = 80;
wall_thickness = 2;

// Base module with water reservoir and heating element
module base() {
    difference() {
        // Main base cylinder
        cylinder(h=base_height, d=body_diameter+6, $fn=64);

        // Hollow interior
        translate([0, 0, wall_thickness])
            cylinder(h=base_height-wall_thickness, d=body_diameter+2, $fn=64);

        // Water fill port
        translate([body_diameter/2 - 8, 0, base_height-10])
            rotate([90, 0, 0])
                cylinder(h=15, d=12, $fn=32);
    }

    // Control panel
    translate([body_diameter/2 - 15, 0, base_height-5])
        cube([25, 40, 5]);

    // Control knobs
    for(i=[-1:1]) {
        translate([body_diameter/2 - 2.5, i*12, base_height-2])
            cylinder(h=8, d=6, $fn=16);
    }

    // Power indicator light
    translate([body_diameter/2 - 15, 20, base_height-3])
        sphere(d=4, $fn=16);
}

// Steaming basket with holes
module basket() {
    difference() {
        // Main basket cylinder
        cylinder(h=steamer_height-base_height-lid_height-wall_thickness, 
                d=body_diameter, $fn=64);

        // Hollow interior
        translate([0, 0, wall_thickness])
            cylinder(h=steamer_height-base_height-lid_height-wall_thickness*2, 
                    d=body_diameter-wall_thickness*2, $fn=64);

        // Steam holes pattern
        for(i=[-2:2]) {
            for(j=[-2:2]) {
                if(i*i + j*j < 2.5) {
                    translate([i*15, j*15, steamer_height/2-base_height])
                        cylinder(h=wall_thickness+1, d=3, $fn=8);
                }
            }
        }
    }

    // Rim/lip for stacking
    translate([0, 0, steamer_height-base_height-lid_height-wall_thickness])
        cylinder(h=wall_thickness, d=body_diameter+4, $fn=64);
}

// Lid with handle
module lid() {
    difference() {
        // Main lid dome
        translate([0, 0, lid_height-5])
            sphere(d=body_diameter+4, $fn=64);

        // Hollow interior
        translate([0, 0, lid_height-3])
            sphere(d=body_diameter, $fn=64);

        // Trim bottom half
        translate([0, 0, lid_height/2-5])
            cube([body_diameter+10, body_diameter+10, lid_height], center=true);
    }

    // Central handle
    translate([0, 0, lid_height-2])
        cylinder(h=15, d=12, $fn=32);

    // Handle grip indent
    translate([0, 0, lid_height+5])
        cylinder(h=10, d=8, $fn=32);

    // Steam vent hole
    translate([0, body_diameter/4, lid_height-2])
        cylinder(h=10, d=4, $fn=8);
}

// Assembly
base();
translate([0, 0, base_height])
    basket();
translate([0, 0, steamer_height-lid_height+5])
    lid();

// Electric Steamer - Medium complexity realistic style
// Approx 120mm width, 120mm depth, 150mm height

// Main parameters
base_width = 120;
base_depth = 120;
base_height = 25;
wall_thickness = 2;

// Module for rounded cube
module rounded_cube(size, radius) {
    translate([radius, radius, 0])
    minkowski() {
        cube([size.x - 2*radius, size.y - 2*radius, size.z]);
        cylinder(r=radius, h=0.01);
    }
}

// Base unit
module steamer_base() {
    difference() {
        // Main base body
        rounded_cube([base_width, base_depth, base_height], 5);

        // Recessed top
        translate([wall_thickness, wall_thickness, base_height - 2])
        cube([base_width - 2*wall_thickness, base_depth - 2*wall_thickness, 5]);

        // Ventilation holes - front
        for(i = [1:5]) {
            translate([20*i, wall_thickness, base_height/2])
            rotate([0, 90, 0])
            cylinder(h=wall_thickness*2, r=3);
        }

        // Ventilation holes - back
        for(i = [1:5]) {
            translate([20*i, base_depth - wall_thickness*3, base_height/2])
            rotate([0, 90, 0])
            cylinder(h=wall_thickness*2, r=3);
        }
    }

    // Control panel
    translate([base_width - 35, base_depth - 20, base_height])
    difference() {
        cube([30, 15, 8]);
        // LED display window
        translate([5, 5, 7.9])
        cube([20, 5, 0.2]);
    }

    // Control buttons
    for(i = [0:2]) {
        translate([base_width - 30 + i*8, base_depth - 10, base_height + 9])
        cylinder(h=3, r=3);
    }
}

// Water reservoir
module water_reservoir() {
    translate([wall_thickness*2, wall_thickness*2, base_height])
    difference() {
        rounded_cube([base_width - wall_thickness*4, base_depth - wall_thickness*4, 25], 3);
        // Hollow inside
        translate([wall_thickness, wall_thickness, wall_thickness])
        rounded_cube([base_width - wall_thickness*6, base_depth - wall_thickness*6, 23], 2);
    }

    // Water level indicator
    translate([wall_thickness*2 - 1, wall_thickness*2 + 5, base_height + 10])
    cube([1, 15, 1]);
}

// Steaming baskets
module steaming_basket(height_offset) {
    translate([0, 0, height_offset])
    difference() {
        // Outer basket
        cylinder(h=30, r=55, $fn=64);
        // Inner hollow
        cylinder(h=30, r=53, $fn=64);

        // Drainage holes in bottom
        for(i = [0:11]) {
            rotate([0, 0, i*30])
            translate([40, 0, 0])
            cylinder(h=30, r=2);
        }
    }

    // Handles
    for(side = [0, 1]) {
        translate([side ? 50 : -50, 0, height_offset + 15])
        rotate([0, 90, 0])
        cylinder(h=10, r=3);
    }
}

// Lid
module steamer_lid() {
    translate([0, 0, base_height + 25 + 30 + 30 + 5])
    difference() {
        // Main lid dome
        scale([1, 1, 0.5])
        sphere(r=60, $fn=64);

        // Hollow inside
        translate([0, 0, -5])
        scale([0.9, 0.9, 0.5])
        sphere(r=60, $fn=64);

        // Handle hole
        translate([0, 0, -10])
        cylinder(h=20, r=5);
    }

    // Lid handle
    translate([0, 0, base_height + 25 + 30 + 30 + 25])
    cylinder(h=20, r=8);

    // Steam vent
    translate([0, 30, base_height + 25 + 30 + 30 + 5])
    cylinder(h=10, r=3);
}

// Assembly
steamer_base();
water_reservoir();
steaming_basket(base_height + 25);
steaming_basket(base_height + 25 + 30);
steamer_lid();

// Hot Plate Kitchen Appliance – Realistic / Medium detail
// -------------------------------------------------------
// Overall size: 110 mm (W) x 80 mm (D) x 35 mm (H)
// Designed for 3D printing: 2 mm walls, no steep overhangs

$fn = 36;               // Smooth curves

wall_th = 2;            // Consistent wall thickness
plate_d = 90;           // Hot-plate diameter
base_w  = 110;          // Body width
base_d  = 80;           // Body depth
base_h  = 25;           // Body height
knob_r  = 7;            // Control knob radius
knob_h  = 10;           // Knob height
foot_r  = 4;            // Rubber foot radius
foot_h  = 3;            // Foot height

// ---------- Rubber feet ----------
module foot() {
    cylinder(h = foot_h, r1 = foot_r, r2 = foot_r * 0.8);
}

// ---------- Heat plate (cast metal look) ----------
module heat_plate() {
    difference() {
        // Main disc
        cylinder(h = 4, r = plate_d/2);
        // Chamfered top edge
        translate([0,0,3.9])
            cylinder(h = 1, r1 = plate_d/2 - 2, r2 = plate_d/2);
    }
}

// ---------- Housing ----------
module body() {
    difference() {
        // Main box
        translate([-base_w/2, -base_d/2, 0])
            cube([base_w, base_d, base_h]);

        // Hollow interior
        translate([-base_w/2 + wall_th,
                   -base_d/2 + wall_th,
                   wall_th])
            cube([base_w - 2*wall_th,
                  base_d - 2*wall_th,
                  base_h]);

        // Top circular opening for plate
        translate([0,0,base_h-1])
            cylinder(h = 5, r = plate_d/2 + 2);
    }
}

// ---------- Control knob with indicator line ----------
module knob() {
    translate([0,0,knob_h/2])
        rotate([90,0,0])
            difference() {
                cylinder(h = knob_h, r = knob_r);
                // Side flat for grip
                translate([0,-knob_r,0])
                    cube([20, knob_r*2, knob_h], center=true);
                // Indicator groove
                translate([0,0,knob_h-2])
                    cylinder(h = 3, r = knob_r-2);
            }
}

// ---------- Assembly ----------
union() {
    // Housing
    body();

    // Heat plate on top
    translate([0,0,base_h-1])
        heat_plate();

    // Control knob on front-right corner
    translate([base_w/2 - knob_r - 5,
               -base_d/2 + knob_r + 5,
               base_h - 2])
        knob();

    // Four rubber feet
    foot_pos = [[-base_w/2 + foot_r + 4, -base_d/2 + foot_r + 4],
                [ base_w/2 - foot_r - 4, -base_d/2 + foot_r + 4],
                [ base_w/2 - foot_r - 4,  base_d/2 - foot_r - 4],
                [-base_w/2 + foot_r + 4,  base_d/2 - foot_r - 4]];
    for(p = foot_pos)
        translate([p.x, p.y, 0])
            foot();
}

