// Grill Pan - Realistic Style, Medium Complexity
// Overall dimensions: ~120mm x 90mm x 30mm

// Parameters
pan_length = 120;
pan_width = 90;
pan_height = 25;
wall_thickness = 2;
handle_length = 60;
handle_diameter = 18;
handle_gap = 8;

// Main pan body
module pan_body() {
    difference() {
        // Outer shell
        cube([pan_length, pan_width, pan_height], center=true);

        // Inner cavity
        translate([0, 0, wall_thickness/2])
        cube([pan_length-wall_thickness*2, pan_width-wall_thickness*2, pan_height], center=true);

        // Drain spout cut
        translate([pan_length/2 - 3, 0, -pan_height/2])
        cube([6, 12, wall_thickness]);
    }
}

// Grill ridges pattern
module grill_ridges() {
    ridge_height = 2;
    ridge_width = 2;
    ridge_spacing = 6;
    num_ridges = floor((pan_width - wall_thickness*2) / ridge_spacing);

    for(i = [0:num_ridges-1]) {
        translate([
            0, 
            -pan_width/2 + wall_thickness + ridge_spacing/2 + i*ridge_spacing, 
            pan_height/2 - ridge_height - 1
        ])
        cube([pan_length - wall_thickness*2 - 10, ridge_width, ridge_height], center=true);
    }
}

// Handle connection block
module handle_block() {
    translate([-pan_length/2 - 10, 0, 0])
    cube([20, 30, 15], center=true);
}

// Main handle
module handle() {
    translate([-pan_length/2 - handle_length/2 - 10, 0, 0])
    rotate([0, 90, 0])
    difference() {
        // Handle grip
        cylinder(h=handle_length, d=handle_diameter, center=true);

        // Grip hole
        cylinder(h=handle_length+2, d=handle_diameter-handle_gap, center=true);
    }
}

// Handle support ribs
module handle_ribs() {
    rib_width = 3;
    rib_height = 8;

    for(i = [-1:1]) {
        translate([
            -pan_length/2 - 20 - i*15, 
            0, 
            -pan_height/2 + rib_height/2 + 2
        ])
        rotate([0, 90, 0])
        cube([rib_width, 25, rib_height], center=true);
    }
}

// Pour spouts
module pour_spouts() {
    spout_height = 4;

    // Left spout
    translate([pan_length/2 - 3, pan_width/2 - 5, pan_height/2 - spout_height/2])
    cube([6, 10, spout_height]);

    // Right spout
    translate([pan_length/2 - 3, -pan_width/2 + 5, pan_height/2 - spout_height/2])
    cube([6, 10, spout_height]);
}

// Base ring for stability
module base_ring() {
    translate([0, 0, -pan_height/2 + 1])
    difference() {
        cube([pan_length - 5, pan_width - 5, 2], center=true);
        cube([pan_length - 15, pan_width - 15, 3], center=true);
    }
}

// Assembly
union() {
    pan_body();
    grill_ridges();
    handle_block();
    handle();
    handle_ribs();
    pour_spouts();
    base_ring();
}

// Cast Iron Pan - Realistic Style, Medium Complexity
// Pan diameter: 150mm, height: 45mm, wall thickness: 2mm

module cast_iron_pan() {
    // Pan body - main cooking surface
    difference() {
        // Outer pan body
        cylinder(h=25, d=150, $fn=64);

        // Inner cavity for cooking surface
        translate([0, 0, 2])
            cylinder(h=23, d=146, $fn=64);
    }

    // Pan bottom - thick base for heat retention
    translate([0, 0, -10])
        cylinder(h=10, d=150, $fn=64);

    // Handle socket - where handle connects to pan
    translate([75, 0, 12.5])
        rotate([0, 90, 0])
            cylinder(h=20, d=25, $fn=32);

    // Handle - cast iron handle with grip pattern
    translate([95, 0, 12.5])
        rotate([0, 90, 0])
            difference() {
                // Main handle body
                cylinder(h=80, d=15, $fn=32);

                // Handle grip grooves
                for(i = [1:8]) {
                    translate([i*10, 0, 0])
                        rotate([0, 90, 0])
                            cube([1, 15, 15], center=true);
                }
            }

    // Handle end hole for hanging
    translate([175, 0, 12.5])
        rotate([0, 90, 0])
            cylinder(h=20, d=8, $fn=32);

    // Pouring spouts (2x)
    for(angle = [30, -30]) {
        rotate([0, 0, angle])
            translate([75, 0, 12.5])
                rotate([0, 90, angle])
                    difference() {
                        // Spout body
                        cube([15, 25, 4], center=true);

                        // Spout inner curve
                        translate([0, -12.5, 0])
                            sphere(d=25, $fn=32);
                    }
    }

    // Reinforcement ring under rim
    translate([0, 0, 23])
        cylinder(h=3, d=154, $fn=64);

    // Manufacturing marks/text on bottom
    translate([0, 0, -9.5])
        linear_extrude(height=0.5)
            text("8\"", size=15, halign="center", valign="center");

    // Heat ring on bottom for stove contact
    translate([0, 0, -10])
        difference() {
            cylinder(h=1, d=150, $fn=64);
            cylinder(h=1, d=130, $fn=64);
        }

    // Additional heat distribution rings
    for(d = [140, 130, 120, 110]) {
        translate([0, 0, -9])
            difference() {
                cylinder(h=0.5, d=d, $fn=64);
                cylinder(h=0.5, d=d-5, $fn=64);
            }
    }
}

// Render the complete pan
cast_iron_pan();

// Non-stick frying pan (Ø120 mm)
// Wall thickness = 2 mm, printable without supports

// Overall scale
pan_scale = 1;

// Pan body dimensions
pan_d = 120 * pan_scale;          // outer diameter
wall_t = 2 * pan_scale;           // wall thickness
pan_h_body = 30 * pan_scale;      // body height
base_d = 80 * pan_scale;          // bottom disk Ø
fillet_r = 4 * pan_scale;         // inside bottom fillet

// Handle
handle_l = 100 * pan_scale;       // handle length
handle_w = 25 * pan_scale;        // handle width
handle_h = 18 * pan_scale;        // handle height
handle_mount_l = 20 * pan_scale;  // mounting block length
handle_mount_h = 22 * pan_scale;

// Rivets for handle mount
rivet_r = 3 * pan_scale;
rivet_h = 2 * pan_scale;
rivet_offset = 8 * pan_scale;

// Helper module for rounded pan body
module pan_body() {
    // Main hollow cylinder
    difference() {
        cylinder(h = pan_h_body, d = pan_d, $fn = 90);
        translate([0, 0, -1])
            cylinder(h = pan_h_body + 2, d = pan_d - 2 * wall_t, $fn = 90);
    }

    // Flat bottom disk
    translate([0, 0, 0])
        cylinder(h = wall_t, d = base_d, $fn = 90);

    // Inside bottom fillet
    translate([0, 0, wall_t])
        difference() {
            cylinder(h = fillet_r, d = base_d - 2 * wall_t, $fn = 90);
            sphere(d = base_d - 2 * wall_t, $fn = 90);
        }
}

// Handle with ergonomic bulge
module handle() {
    // Mounting block on pan
    translate([-pan_d/2 - handle_mount_l/2, 0, pan_h_body/2])
        cube([handle_mount_l, handle_w, handle_mount_h], center = true);

    // Main handle bar
    translate([-pan_d/2 - handle_mount_l - handle_l/2, 0, pan_h_body/2])
        cube([handle_l, handle_w * 0.8, handle_h * 0.7], center = true);

    // Top ergonomic ridge
    translate([-pan_d/2 - handle_mount_l - handle_l/2, 0, pan_h_body/2 + handle_h/2])
        cube([handle_l, handle_w * 0.5, handle_h * 0.3], center = true);
}

// Rivets securing handle
module rivets() {
    for (y = [-rivet_offset, rivet_offset])
        translate([-pan_d/2 - rivet_h/2, y, pan_h_body/2])
            rotate([0, 90, 0])
                cylinder(h = rivet_h, d = rivet_r, $fn = 20);
}

// Assemble the pan
union() {
    pan_body();
    handle();
    rivets();
}

$fn = 36;

// overall scale factor for easy resizing
scale_factor = 1;

// saucepan parameters
body_d = 90 * scale_factor;
body_h = 45 * scale_factor;
wall_t = 2 * scale_factor;
bottom_t = 3 * scale_factor;

// handle parameters
handle_l = 80 * scale_factor;
handle_base_w = 12 * scale_factor;
handle_base_h = 8 * scale_factor;
handle_mid_w = 6 * scale_factor;
handle_end_w = 10 * scale_factor;
handle_curve_r = 10 * scale_factor;
handle_clearance = 3 * scale_factor;

// lid parameters
lid_top_d = body_d + 2 * wall_t;
lid_flange_h = 5 * scale_factor;
lid_knob_h = 12 * scale_factor;
lid_knob_d = 14 * scale_factor;

// rim lip
lip_h = 4 * scale_factor;

// saucepan body module
module saucepan_body() {
    difference() {
        // outer shell
        cylinder(h = body_h + lip_h, d = body_d, center = false);

        // inner cavity
        translate([0, 0, bottom_t])
            cylinder(h = body_h - bottom_t, d = body_d - 2 * wall_t, center = false);

        // remove inside bottom
        translate([0, 0, 0])
            cylinder(h = bottom_t, d = body_d - 2 * wall_t, center = false);
    }

    // add bottom thickness
    translate([0, 0, 0])
        cylinder(h = bottom_t, d = body_d, center = false);

    // add rim lip outside
    translate([0, 0, body_h])
        cylinder(h = lip_h, d = body_d + 2 * wall_t, center = false);

    // add rim lip inside
    translate([0, 0, body_h])
        cylinder(h = lip_h, d = body_d, center = false);
}

// handle module
module handle() {
    // base block
    translate([body_d / 2 - wall_t - handle_base_w / 2, 0, (body_h + lip_h) / 2])
        cube([handle_base_w, handle_base_h, handle_base_h], center = true);

    // curved middle section using hull of cylinders
    hull() {
        translate([body_d / 2 + handle_clearance, 0, (body_h + lip_h) / 2])
            rotate([0, 90, 0])
                cylinder(h = handle_base_h, d = handle_base_w, center = true);

        translate([body_d / 2 + handle_l / 2, 0, (body_h + lip_h) / 2 + handle_curve_r])
            rotate([0, 90, 0])
                cylinder(h = handle_mid_w, d = handle_mid_w, center = true);

        translate([body_d / 2 + handle_l, 0, (body_h + lip_h) / 2])
            rotate([0, 90, 0])
                cylinder(h = handle_end_w, d = handle_end_w, center = true);
    }
}

// lid module
module lid() {
    // main lid disc
    cylinder(h = lid_flange_h, d = lid_top_d, center = false);

    // inner flange
    translate([0, 0, lid_flange_h])
        cylinder(h = 2 * wall_t, d = body_d - 0.5, center = false);

    // knob
    translate([0, 0, lid_flange_h + 2 * wall_t])
        cylinder(h = lid_knob_h, d = lid_knob_d, center = false);

    // knob sphere top
    translate([0, 0, lid_flange_h + 2 * wall_t + lid_knob_h])
        sphere(d = lid_knob_d, center = true);
}

