// Main pot body module
module stockpot_body() {
    difference() {
        // Outer body
        cylinder(h=pot_height, r=pot_radius, $fn=64);

        // Hollow interior
        translate([0, 0, -1])
            cylinder(h=pot_height+2, r=pot_radius-wall_thickness, $fn=64);

        // Flat base inside
        translate([0, 0, base_thickness])
            cylinder(h=pot_height-base_thickness, r=pot_radius-wall_thickness, $fn=64);
    }

    // Reinforced base ring
    translate([0, 0, -1])
        cylinder(h=base_thickness+1, r=pot_radius+1, $fn=64);
}

// Handle module
module pot_handle(side) {
    translate([side * (pot_radius+handle_length/2), 0, pot_height/2]) {
        rotate([0, 90, 0]) {
            // Handle grip
            hull() {
                translate([-handle_length/2, 0, -handle_width/2])
                    sphere(r=handle_width/2, $fn=16);
                translate([handle_length/2, 0, -handle_width/2])
                    sphere(r=handle_width/2, $fn=16);
                translate([-handle_length/2, 0, handle_width/2])
                    sphere(r=handle_width/2, $fn=16);
                translate([handle_length/2, 0, handle_width/2])
                    sphere(r=handle_width/2, $fn=16);
            }

            // Handle brackets (2 per handle)
            for(pos=[-handle_length/3, handle_length/3]) {
                translate([pos, 0, -handle_width/2-2])
                    cube([wall_thickness*2, handle_height, wall_thickness*2], center=true);
            }
        }
    }
}

// Lid module
module stockpot_lid() {
    difference() {
        // Main lid
        translate([0, 0, pot_height+2])
            cylinder(h=lid_height, r=pot_radius-wall_thickness/2, $fn=64);

        // Inner lip
        translate([0, 0, pot_height+2])
            cylinder(h=lid_height/2, r=pot_radius-wall_thickness*2, $fn=64);

        // Steam vent hole
        translate([0, 0, pot_height+2+lid_height])
            cylinder(h=5, r=2, $fn=16);
    }

    // Lid knob
    translate([0, 0, pot_height+2+lid_height])
        cylinder(h=lid_knob_height, r=lid_knob_radius, $fn=32);

    // Knob grip ring
    translate([0, 0, pot_height+2+lid_height+lid_knob_height/2])
        difference() {
            cylinder(h=lid_knob_height/2, r=lid_knob_radius*1.5, $fn=32);
            cylinder(h=lid_knob_height/2+1, r=lid_knob_radius*1.1, $fn=32);
        }
}

// Rim reinforcement
module rim_reinforcement() {
    translate([0, 0, pot_height-3])
        cylinder(h=5, r=pot_radius+1, $fn=64);

    difference() {
        translate([0, 0, pot_height-3])
            cylinder(h=5, r=pot_radius+2, $fn=64);
        translate([0, 0, pot_height-5])
            cylinder(h=10, r=pot_radius+1, $fn=64);
    }
}

// Assembly
union() {
    // Main pot
    stockpot_body();

    // Rim details
    rim_reinforcement();

    // Side handles
    pot_handle(1);
    pot_handle(-1);

    // Lid (can be printed separately)
    stockpot_lid();
}

