        hull() {
            translate([body_width/2-wall, body_thickness/2-wall, 0])
                cylinder(h=body_thickness, r=wall, $fn=20);
            translate([-body_width/2+wall, body_thickness/2-wall, 0])
                cylinder(h=body_thickness, r=wall, $fn=20);
            translate([body_width/2-wall, -body_thickness/2+wall, 0])
                cylinder(h=body_thickness, r=wall, $fn=20);
            translate([-body_width/2+wall, -body_thickness/2+wall, 0])
                cylinder(h=body_thickness, r=wall, $fn=20);
        }

        // Hollow out interior
        hull() {
            translate([body_width/2-2*wall, body_thickness/2-2*wall, wall])
                cylinder(h=body_thickness-2*wall, r=wall-1, $fn=20);
            translate([-body_width/2+2*wall, body_thickness/2-2*wall, wall])
                cylinder(h=body_thickness-2*wall, r=wall-1, $fn=20);
            translate([body_width/2-2*wall, -body_thickness/2+2*wall, wall])
                cylinder(h=body_thickness-2*wall, r=wall-1, $fn=20);
            translate([-body_width/2+2*wall, -body_thickness/2+2*wall, wall])
                cylinder(h=body_thickness-2*wall, r=wall-1, $fn=20);
