$fn = 48;

// ------------------ MODULES ------------------
module motor_base() {
    // Main cylindrical body
    cylinder(h = base_h, d = base_d);

    // Chamfer top edge
    translate([0, 0, base_h])
    cylinder(h = 3, d1 = base_d, d2 = base_d - 4);

    // Vent slots (cut-outs)
    for (a = [0 : 60 : 300]) {
        rotate([0, 0, a])
        translate([base_d/2 - 1, -3, base_h/2])
        cube([wall + 1, 6, 8], center = true);
    }

    // Rubber feet
    for (a = [45 : 90 : 315]) {
        rotate([0, 0, a])
        translate([base_d/2 * 0.75, 0, -0.5])
        cylinder(h = 2.5, d = 6);
    }
}

module control_panel() {
    // Panel plate
    translate([base_d/2 - 1, 0, base_h/2])
    rotate([0, 90, 0])
    cube([base_h - 10, 20, wall]);

    // Buttons
    for (i = [0 : 1]) {
        translate([base_d/2 + 1, -6 + i*12, base_h/2 - 5 + i*8])
        cylinder(h = button_h, r = button_r);
    }
}

module jar() {
    // Main jar body (hollow tube)
    difference() {
        cylinder(h = jar_h, d = jar_d);
        translate([0, 0, wall])
        cylinder(h = jar_h, d = jar_d - 2*wall);
    }

    // Bottom threads ring
    translate([0, 0, -wall])
    cylinder(h = wall, d = jar_d + 4);

    // Handle
    translate([jar_d/2 + 2, 0, jar_h/2])
    rotate([0, 90, 0])
    difference() {
        cylinder(h = 30, d = 12);
        cylinder(h = 30, d = 8);
    }
}

module lid() {
    difference() {
        // Lid cap
        cylinder(h = lid_h, d = lid_d + 4);

        // Recess for stacking
        translate([0, 0, wall])
        cylinder(h = lid_h - wall, d = lid_d);

        // Center hole for pouring
        translate([0, 0, lid_h/2])
        rotate([0, 90, 0])
        cylinder(h = 20, r = 3);
    }

    // Small grip handle
    translate([0, lid_d/2 - 4, lid_h - 2])
    cube([20, 8, 4], center = true);
}

