                rotate([0, 0, i * 120])
                    translate([pusher_d/2 - 4, 0, 15])
                        cube([8, 4, 20], center = true);
        }

    // Juice jar
    translate([base_d/2 + jar_d/2 - 5, 0, 0])
        difference() {
            cylinder(h = jar_h, d = jar_d, $fn = 48);
            // Internal cavity
