// Realistic medium-complexity kitchen smoker (≈90×70×100 mm)

$fn = 24;               // Smoothed curves
wall = 2;               // 3D-printable wall thickness
body_w = 90;            // Main body width
body_d = 70;            // Main body depth  
body_h = 90;            // Main body height

// Main body shell
module body_shell() {
    difference() {
        // Outer shell
        cube([body_w, body_d, body_h], center = true);
        // Hollow inside
        translate([0, 0, wall/2])
            cube([body_w-wall*2, body_d-wall*2, body_h-wall], center = true);
    }
}

// Front door with handle
module door() {
    translate([0, -body_d/2 - wall/2, 0])
    rotate([90, 0, 0])
    difference() {
        // Door panel
        translate([0, 0, -body_h/2 + 10])
            cube([body_w-20, body_h-20, wall*2], center = true);
        // Window
        translate([0, 0, -wall])
            cube([body_w-40, body_h-40, wall*2+1], center = true);
    }
    // Handle
    translate([body_w/2-15, -body_d/2 - wall*2, 0])
        rotate([90, 0, 0])
            cylinder(h = 10, d = 6);
}

// Side smoke stack
module smoke_stack() {
    translate([body_w/2 - 15, 0, body_h/2]) {
        // Base
        cylinder(h = 8, d1 = 20, d2 = 14);
        // Pipe
        translate([0, 0, 8])
            cylinder(h = 25, d = 10);
        // Cap
        translate([0, 0, 33])
            cylinder(h = 4, d1 = 14, d2 = 10);
    }
}

// Control panel with knobs
module control_panel() {
    translate([body_w/2 + wall/2, 0, body_h/2 - 30])
        rotate([0, 90, 0])
            cube([body_d-10, 35, wall*2]);
    // Knobs
    for (i = [0:2]) {
        translate([body_w/2 + wall*2, -20 + i*20, body_h/2 - 25])
            rotate([0, 90, 0])
                cylinder(h = 6, d = 10);
    }
}

// Legs
module legs() {
    leg_x = body_w/2 - 10;
    leg_y = body_d/2 - 10;
    leg_z = -body_h/2 - 5;
    for (x = [-leg_x, leg_x])
        for (y = [-leg_y, leg_y])
            translate([x, y, leg_z])
                cube([8, 8, 10], center = true);
}

// Grill racks
module grill_racks() {
    rack_y = body_d/2 - 15;
    for (z = [-20, 10]) {
        translate([0, 0, z])
            for (x = [-body_w/2+15 : 10 : body_w/2-15])
                cube([2, body_d-30, 2], center = true);
    }
}

// Temperature gauge
module gauge() {
    translate([0, body_d/2 - wall, body_h/2 - 10])
        rotate([90, 0, 0]) {
            cylinder(h = 8, d = 14);
            translate([0, 0, 8])
                cylinder(h = 2, d = 10);
        }
}

// Assemble smoker
union() {
    body_shell();
    door();
    smoke_stack();
    control_panel();
    legs();
    grill_racks();
    gauge();
}

// Raclette Kitchen Appliance - Realistic Style, Medium Complexity
// Overall dimensions: 170mm x 80mm x 90mm

$fn = 32;

// Main body module
module raclette_body() {
    // Base housing
    difference() {
        // Main housing block
        translate([0, 0, 40]) {
            cube([170, 80, 50], center = true);
        }

        // Top recess for grill plate
        translate([0, 0, 65]) {
            cube([160, 70, 10], center = true);
        }

        // Front display recess
        translate([0, 35, 55]) {
            cube([80, 5, 15], center = true);
        }

        // Ventilation slots on sides
        for (i = [-1, 1]) {
            translate([i * 85, 0, 30]) {
                for (j = [-20, 0, 20]) {
                    translate([0, j, 0]) {
                        cube([2, 10, 15], center = true);
                    }
                }
            }
        }
    }
}

// Control panel module
module control_panel() {
    // Display screen
    color([0.2, 0.2, 0.2]) {
        translate([0, 38, 55]) {
            cube([60, 2, 8], center = true);
        }
    }

    // Control knobs
    for (i = [-1, 1]) {
        translate([i * 25, 38, 50]) {
            cylinder(h = 5, r = 6, center = true);
            translate([0, 3, 0]) {
                cube([2, 6, 2], center = true);
            }
        }
    }

    // Power indicator LED
    translate([40, 38, 55]) {
        color([1, 0, 0]) {
            sphere(r = 3);
        }
    }
}

// Grill plate module
module grill_plate() {
    // Plate base
    translate([0, 0, 70]) {
        cube([160, 70, 5], center = true);
    }

    // Grill ridges
    for (i = [-5:5]) {
        translate([0, i * 12 - 6, 73]) {
            cube([150, 3, 2], center = true);
        }
    }

    // Drip edge
    translate([0, 40, 70]) {
        cube([160, 5, 8], center = true);
    }
}

// Raclette pans module
module raclette_pans() {
    for (i = [-1.5, -0.5, 0.5, 1.5]) {
        translate([i * 35, 0, 82]) {
            // Pan base
            difference() {
                cube([30, 60, 8], center = true);
                translate([0, 0, 4]) {
                    cube([26, 56, 4], center = true);
                }
            }

            // Handle
            translate([0, -35, 0]) {
                rotate([90, 0, 0]) {
                    cylinder(h = 15, r1 = 3, r2 = 3, center = true);
                }
            }
        }
    }
}

// Support feet module
module support_feet() {
    for (x = [-1, 1]) {
        for (y = [-1, 1]) {
            translate([x * 70, y * 30, 0]) {
                cylinder(h = 8, r = 5, center = true);
            }
        }
    }
}

// Power cable
module power_cable() {
    translate([-85, 0, 30]) {
        rotate([0, 90, 0]) {
            difference() {
                cylinder(h = 30, r = 4, center = true);
                cylinder(h = 30, r = 2, center = true);
            }
        }
    }
}

// Main assembly
union() {
    color([0.9, 0.9, 0.9]) {
        raclette_body();
    }

    color([0.3, 0.3, 0.3]) {
        control_panel();
    }

    color([0.4, 0.4, 0.4]) {
        grill_plate();
    }

    color([0.8, 0.8, 0.8]) {
        raclette_pans();
    }

    color([0.2, 0.2, 0.2]) {
        support_feet();
    }

    color([0.1, 0.1, 0.1]) {
        power_cable();
    }
}

