            cube([body_width, body_depth, 5], center=true);
            translate([0, 0, -2.5])
                cube([body_width-wall_thickness*2, body_depth-wall_thickness*2, 10], center=true);
        }
}

module handle_assembly() {
    // Crank handle
    translate([-body_width/2 - handle_length/2, 0, 0])
        rotate([90, 0, 0])
            cylinder(h=handle_length, d=handle_diameter, center=true);

    // Handle grip
    translate([-body_width/2 - handle_length - 10, 0, 0])
        sphere(d=20);

    // Crank arm
    translate([-body_width/2 - 5, 0, 0])
        cube([10, 10, 20], center=true);
}

module blade_disc() {
    // Main blade disc
    rotate([0, 0, $t*360])
    translate([0, 0, body_height/2 + 10])
        difference() {
            cylinder(h=blade_thickness, d=blade_diameter, center=true);

            // Central hole
            cylinder(h=blade_thickness+1, d=15, center=true);

            // Cutting teeth
            for(i=[0:blade_teeth-1]) {
                rotate([0, 0, i*360/blade_teeth])
                    translate([blade_diameter/2 - 8, 0, 0])
                        cube([16, 3, blade_thickness+1], center=true);
            }
        }
}

module food_container() {
    // Main container
    difference() {
        translate([0, body_depth/2 + container_depth/2 + 10, 0])
            cube([container_width, container_depth, container_height], center=true);
        translate([0, body_depth/2 + container_depth/2 + 10, -wall_thickness/2])
            cube([container_width-wall_thickness*2, container_depth-wall_thickness*2, container_height-wall_thickness], center=true);
    }

    // Container lip
    translate([0, body_depth/2 + container_depth/2 + 10, container_height/2 + 2])
        cube([container_width+4, container_depth+4, 4], center=true);
}

module suction_base() {
    // Base with suction cups
    translate([0, 0, -body_height/2 - base_height/2])
        difference() {
            cylinder(h=base_height, d=base_diameter, center=true);

            // Suction cup recesses
            for(x=[-30, 30]) {
                for(y=[-20, 20]) {
                    translate([x, y, -1])
                        cylinder(h=base_height+2, d=15, center=false);
                }
            }
        }
}

module control_knob() {
    // Speed control knob
    translate([body_width/2 - 15, -body_depth/2 + 15, body_height/2 + 5])
        difference() {
            cylinder(h=knob_height, d=knob_diameter, center=false);
            translate([0, 0, -1])
                cylinder(h=knob_height+2, d=8, center=false);
        }

    // Knob indicator mark
    translate([body_width/2 - 15, -body_depth/2 + 15, body_height/2 + knob_height + 1])
        cube([2, 6, 2], center=true);
}

module feed_tube() {
    // Vertical feed tube
    translate([-body_width/4, 0, body_height/2 + 25])
        difference() {
            cylinder(h=30, d=25, center=true);
            cylinder(h=31, d=20, center=true);
        }

    // Food pusher
    translate([-body_width/4, 0, body_height/2 + 40])
        cylinder(h=20, d=18, center=true);
}

// Assembly
