    // Base
    difference() {
        cylinder(h=h_base, r=r_base, $fn=60);
        translate([0,0,wall])
            cylinder(h=h_base, r=r_base-wall, $fn=60);
    }

    // Cylindrical tank
    translate([0,0,h_base])
    difference() {
        cylinder(h=h_body, r=r_body, $fn=60);
        translate([0,0,wall])
            cylinder(h=h_body, r=r_body-wall, $fn=60);
    }

    // Lid
    translate([0,0,h_base+h_body])
    difference() {
        cylinder(h=wall, r=r_body, $fn=60);
        translate([0,0,wall/2])
            cylinder(h=wall, r=r_body-wall, $fn=60);
    }

    // Handle on lid (knob)
    translate([0,0,h_base+h_body+h_knob])
    difference() {
        cylinder(h=h_knob, r=r_knob, $fn=30);
        translate([0,0,wall])
            cylinder(h=h_knob, r=r_knob-wall, $fn=30);
    }

    // Spout (hollow cylinder angled 30°)
    translate([r_body,0,h_base+h_body/2])
    rotate([0,30,0])
    difference() {
        cylinder(h=l_spout, r=r_spout, $fn=20);
        translate([0,0,wall])
            cylinder(h=l_spout, r=r_spout-wall, $fn=20);
