    translate([0,r_body-2,h_base+h_body/2])
    rotate([90,0,0])
    difference() {
        cylinder(h=4, r=6, $fn=20);
        translate([0,0,wall])
            cylinder(h=4, r=6-wall, $fn=20);
    }

    // 4 support legs
    for(a=[0,90,180,270])
    rotate([0,0,a])
    translate([r_base-leg_r-3,0,0])
    difference() {
        cylinder(h=leg_h, r=leg_r, $fn=20);
        translate([0,0,wall])
            cylinder(h=leg_h, r=leg_r-wall, $fn=20);
