// Module for rounded cylinder (helps avoid overhangs)
module rounded_cylinder(h, r, rounding=2) {
    union() {
        cylinder(h=h-2*rounding, r=r, $fn=48);
        translate([0,0,h-rounding]) cylinder(h=rounding, r1=r, r2=r-rounding, $fn=48);
        translate([0,0,0]) cylinder(h=rounding, r1=r-rounding, r2=r, $fn=48);
    }
}

// Main kettle body
module kettle_body() {
    difference() {
        rounded_cylinder(h=80, r=kettle_width/2);           // Outer shell
        translate([0,0,wall]) rounded_cylinder(h=80-wall, r=kettle_width/2-wall); // Inner cavity
    }
}

// Spout with gradual slope (no overhangs >45°)
module spout() {
    translate([kettle_width/2-10,0,50]) rotate([0,-30,0]) {
        difference() {
            cylinder(h=45, r1=10, r2=7, $fn=32);
            translate([0,0,wall]) cylinder(h=45-wall, r1=10-wall, r2=7-wall, $fn=32);
        }
    }
}

// Handle loop
