    translate([-kettle_width/2-15,0,50]) rotate([90,0,0]) {
        difference() {
            rounded_cylinder(h=70, r=8);
            translate([0,0,wall]) rounded_cylinder(h=70, r=8-wall);
        }
    }
}

// Lid with knob
module lid() {
    difference() {
        cylinder(h=8, r=kettle_width/2-1, $fn=48);               // Lid disc
        translate([0,0,wall]) cylinder(h=8, r=kettle_width/2-1-wall, $fn=48);
