// Medium complexity realistic steam whistle kettle for 3D printing

$fn = 36;

// Main parameters
kettle_height = 110;
kettle_radius = 35;
wall_thickness = 2;

module kettle_body() {
    difference() {
        // Outer shell
        cylinder(h = kettle_height, r = kettle_radius, center = false);

        // Inner cavity
        translate([0, 0, wall_thickness])
            cylinder(h = kettle_height - wall_thickness, r = kettle_radius - wall_thickness, center = false);
    }
}

module base() {
    // Bottom base ring
    difference() {
        cylinder(h = 8, r = kettle_radius + 5, center = false);
        translate([0, 0, wall_thickness])
            cylinder(h = 8, r = kettle_radius - 5, center = false);
    }
}

module handle() {
    // Main handle grip
    translate([kettle_radius + 10, 0, kettle_height/2])
        rotate([0, 90, 0])
            difference() {
                cylinder(h = 40, r = 8, center = true);
                cylinder(h = 45, r = 6, center = true);
            }

    // Handle supports
    translate([kettle_radius, 0, kettle_height*0.7])
        rotate([90, 0, 0])
            cylinder(h = 15, r = 4, center = false);

    translate([kettle_radius, 0, kettle_height*0.3])
        rotate([90, 0, 0])
            cylinder(h = 15, r = 4, center = false);
}

module spout() {
    // Main spout
    translate([0, -kettle_radius, kettle_height*0.6])
        rotate([30, 0, 0])
            difference() {
                cylinder(h = 35, r1 = 12, r2 = 8, center = false);
                translate([0, 0, wall_thickness])
                    cylinder(h = 35, r1 = 10, r2 = 6, center = false);
            }

    // Spout support
    translate([0, -kettle_radius + 5, kettle_height*0.5])
        cube([10, 10, 15], center = true);
}

module lid() {
    // Lid disk
    translate([0, 0, kettle_height])
        difference() {
            cylinder(h = 6, r = kettle_radius - 1, center = false);
            translate([0, 0, wall_thickness])
                cylinder(h = 8, r = kettle_radius - 3, center = false);
        }

    // Lid handle
    translate([0, 0, kettle_height + 8])
        cylinder(h = 15, r = 5, center = false);

    // Steam vent holes
    for(i = [0:5]) {
        rotate([0, 0, i*60])
            translate([kettle_radius/2, 0, kettle_height + 6])
                cylinder(h = 3, r = 2, center = false);
    }
}

module whistle_mechanism() {
    // Whistle cap
    translate([0, 0, kettle_height + 23])
        difference() {
            cylinder(h = 10, r = 10, center = false);
            cylinder(h = 12, r = 8, center = false);
        }

    // Whistle vents
    for(i = [0:3]) {
        rotate([0, 0, i*90])
            translate([8, 0, kettle_height + 25])
                cube([8, 2, 6], center = true);
    }
}

// Button details
module power_button() {
    translate([kettle_radius - 5, 0, kettle_height*0.2])
        difference() {
            cylinder(h = 5, r = 6, center = false);
            cylinder(h = 6, r = 4, center = false);
        }
}

// Water level indicator
module level_indicator() {
    translate([kettle_radius - 1, 0, kettle_height*0.4])
        difference() {
            cube([4, 20, 25], center = true);
            cube([5, 22, 27], center = true);
        }
}

// Assembly
union() {
    kettle_body();
    base();
    handle();
    spout();
    lid();
    whistle_mechanism();
    power_button();
    level_indicator();
}

