// Kettleglow - a modern electric kettle with glowing LED ring
// Overall height: 120mm, base diameter: 80mm

// Parameters
wall = 2;           // wall thickness for 3D printing
base_d = 80;        // base diameter
base_h = 20;        // base height
body_d = 70;        // main body diameter
body_h = 60;        // main body height
neck_d = 50;        // neck diameter
neck_h = 15;        // neck height
lid_d = 52;         // lid diameter
lid_h = 8;          // lid height
spout_l = 35;       // spout length
spout_d = 18;       // spout diameter
handle_w = 25;      // handle width
handle_h = 50;      // handle height
glow_ring_h = 4;    // LED ring height
button_size = 6;    // power button size

// Main module
module kettleglow() {
    // Base with power button
    union() {
        // Main base cylinder
        cylinder(h=base_h, d=base_d, $fn=64);

        // Power button
        translate([base_d/2 - button_size/2 - 5, 0, base_h - button_size/2 - 2])
            cube([button_size, button_size, button_size], center=true);
    }

    // Main body
    translate([0, 0, base_h])
        cylinder(h=body_h, d=body_d, $fn=64);

    // Neck
    translate([0, 0, base_h + body_h])
        cylinder(h=neck_h, d1=body_d, d2=neck_d, $fn=64);

    // LED glow ring
    translate([0, 0, base_h + body_h - glow_ring_h/2])
        difference() {
            cylinder(h=glow_ring_h, d=body_d + 4, $fn=64);
            cylinder(h=glow_ring_h + 0.1, d=body_d, $fn=64);
        }

    // Lid with knob
    translate([0, 0, base_h + body_h + neck_h])
        union() {
            cylinder(h=lid_h, d=lid_d, $fn=64);
            // Lid knob
            translate([0, 0, lid_h])
                cylinder(h=8, d=12, $fn=32);
        }

    // Spout
    translate([body_d/2 - 5, 0, base_h + body_h/2])
        rotate([0, -30, 0])
        union() {
            // Spout body
            cylinder(h=spout_l, d=spout_d, $fn=32);
            // Spout opening
            translate([0, 0, spout_l])
                cylinder(h=spout_d, d1=spout_d, d2=spout_d - 4, $fn=32);
        }

    // Handle
    translate([-body_d/2 - 5, 0, base_h + body_h/2])
        difference() {
            // Main handle shape
            rotate([0, 90, 0])
                cylinder(h=handle_w, d=handle_h, $fn=32);
            // Handle grip hole
            rotate([0, 90, 0])
                translate([0, 0, -1])
                    cylinder(h=handle_w + 2, d=handle_h - 8, $fn=32);
        }

    // Structural ribs inside body
    for(i = [0:45:315]) {
        rotate([0, 0, i])
            translate([0, 0, base_h])
                cube([wall, body_d/2 - 10, body_h], center=true);
    }
}

// Render the kettle
kettleglow();

