// Kettlecore - Electric Kettle Heating Element Base
// Medium complexity realistic 3D printable model

$fn = 32;

// Main dimensions
base_dia = 45;
base_height = 8;
core_dia = 35;
core_height = 12;
handle_width = 25;
handle_height = 15;
handle_thickness = 3;
button_dia = 6;
led_dia = 3;
wall_thickness = 2;

// Base structure
module kettle_base() {
    // Main circular base
    difference() {
        cylinder(h=base_height, d=base_dia, center=false);
        // Hollow center
        translate([0, 0, wall_thickness])
            cylinder(h=base_height, d=base_dia - wall_thickness*2, center=false);
    }

    // Bottom plate with feet
    translate([0, 0, -wall_thickness])
        difference() {
            cylinder(h=wall_thickness, d=base_dia, center=false);
            // Cable groove
            translate([base_dia/2 - 8, 0, -1])
                cube([16, 8, wall_thickness + 2], center=true);
        }
}

// Heating core assembly
module heating_core() {
    // Central heating element
    difference() {
        cylinder(h=core_height, d=core_dia, center=false);
        // Internal cavity
        translate([0, 0, wall_thickness])
            cylinder(h=core_height - wall_thickness, d=core_dia - wall_thickness*2, center=false);
    }

    // Heating fins
    for (i = [0:45:315]) {
        rotate([0, 0, i])
            translate([core_dia/2 - 2, -1.5, wall_thickness])
                cube([6, 3, core_height - wall_thickness*2], center=false);
    }
}

// Control handle
module control_handle() {
    // Main handle body
    translate([base_dia/2 + 5, 0, base_height/2])
        rotate([90, 0, 0])
            difference() {
                cylinder(h=handle_height, d=handle_thickness*2, center=true);
                translate([0, 0, -handle_height/2])
                    cube([handle_width, handle_thickness*2, handle_height], center=true);
            }

    // Power button
    translate([base_dia/2 + 12, -handle_width/3, base_height + 2])
        cylinder(h=2, d=button_dia, center=false);

    // LED indicator
    translate([base_dia/2 + 12, handle_width/3, base_height + 2])
        cylinder(h=1, d=led_dia, center=false);
}

// Cable entry point
module cable_port() {
    translate([base_dia/2 - 5, 0, base_height/2])
        rotate([90, 0, 90])
            difference() {
                cylinder(h=10, d=8, center=true);
                cylinder(h=12, d=4, center=true);
            }
}

// Mounting holes for assembly
module mounting_holes() {
    for (i = [0:120:240]) {
        rotate([0, 0, i])
            translate([base_dia/2 - 8, 0, -1])
                cylinder(h=base_height + 2, d=3.2, center=false);
    }
}

// Assembly
union() {
    kettle_base();
    translate([0, 0, base_height - wall_thickness]) {
        heating_core();
    }
    control_handle();
    cable_port();

    // Remove mounting holes
    difference() {
        children();
        mounting_holes();
    }
}

