body_depth = 120;
body_height = 140;
wall_thickness = 2;

// Main body - housing
module main_body() {
    difference() {
        // Outer shell
        cube([body_width, body_depth, body_height], center=true);

        // Hollow interior
        translate([0, 0, wall_thickness/2])
            cube([body_width-wall_thickness*2, body_depth-wall_thickness*2, body_height-wall_thickness], center=true);

        // Top opening for juicer
        translate([0, -30, body_height/2-wall_thickness])
            cylinder(h=wall_thickness*2, r=25, center=true);
    }
}

