    translate([0, 30, -body_height/4])
    difference() {
        // Outer container
        cube([50, 50, 40], center=true);
        // Hollow space
        translate([0, 0, -wall_thickness])
            cube([50-wall_thickness*2, 50-wall_thickness*2, 40-wall_thickness], center=true);
    }
}

// Press mechanism
module press_mechanism() {
    // Central shaft
    translate([0, -30, 0])
    cylinder(h=body_height/2, r=10, center=false);

    // Press plate
    translate([0, -30, body_height/2-10])
    difference() {
