// AROMAWAVE essential-oil diffuser / aroma-wave kitchen appliance  
// Realistic style, medium complexity, 3D-printable  
// External size ~110 mm tall, 90 mm diameter  

// ----- PARAMETERS ----------------------------------------------------------
$fn = 36;                 // smooth curves
wall = 2;                 // 3D-print friendly wall thickness
baseH = 25;               // foot height
bodyH = 55;               // main body height
lidH  = 15;               // top lid height
dia   = 90;               // outer diameter
spoutW = 8;               // vapor spout width
buttonD = 3;              // push-button depth

// ----- MODULES -------------------------------------------------------------

module footRing(h, rOut, rIn){
    difference(){
        cylinder(h = h, r = rOut, center = true);
        translate([0,0,-h/2])
            cylinder(h = h+0.1, r = rIn, center = true);
    }
}

module sideButtons(){
    // three identical buttons around the front
    for(a=[-45, 0, 45]){
        rotate([0,0,a])
            translate([dia/2+0.5,0,baseH+bodyH/2])
                rotate([-90,0,0])
                    cube([6,3,buttonD], center=true);
    }
}

module spout(){
    // chimney-like vapor exit on top
    difference(){
        // main spout tube
        translate([0,dia/4,lidH-2])
            rotate([90,0,0])
                cylinder(h=15, r1=spoutW/2, r2=spoutW/2+2, center=false);
        // hollow core
        translate([0,dia/4+0.1,lidH-2])
            rotate([90,0,0])
                cylinder(h=16, r1=spoutW/2-2, r2=spoutW/2, center=false);
    }
}

module gridVents(){
    // vertical slots around body for air intake
    slotH = 10;
    slotW = 2;
    slots = 12;
    for(i=[0:slots-1])
        rotate([0,0,i*360/slots])
            translate([dia/2-wall, 0, baseH+bodyH/2-slotH/2])
                cube([wall+0.1, slotW, slotH], center=true);
}

// ----- MAIN BODY -----------------------------------------------------------

// 1. Anti-slip rubber foot (solid ring)
footRing(baseH, dia/2, dia/2-wall);

// 2. Main cylindrical body with air vents
difference(){
    cylinder(h=bodyH, r=dia/2, center=false);
    // center hollow for water tank
    translate([0,0,baseH+wall])
        cylinder(h=bodyH-wall, r=dia/2-wall, center=false);
    // cut vents
    gridVents();
}

// 3. Control buttons on front
sideButtons();

// 4. Top lid with spout
difference(){
    // lid disc
    translate([0,0,baseH+bodyH])
        cylinder(h=lidH, r1=dia/2, r2=dia/2-2, center=false);
    // hollow cone inside
    translate([0,0,baseH+bodyH])
        cylinder(h=lidH+0.1, r1=dia/2-wall, r2=dia/2-wall-2, center=false);
}

// 5. Vapor spout
spout();

// Kettlesteam – medium-detail, 3D-printable electric kettle
// Overall ~100 mm high, all walls 2 mm thick, no steep overhangs

$fn = 48;               // smooth curves

// ---------------- Parameters ----------------
wall = 2;               // uniform wall thickness
h_body = 75;            // main kettle height
r_body = 45;            // kettle radius
h_base = 12;            // base stand height
r_base = 50;            // base radius
h_lid = 8;              // lid height
r_lid = r_body - wall;  // lid fits inside rim
h_spout = 35;           // spout tip height
l_spout = 40;           // spout forward reach
r_spout = 6;            // spout radius
r_handle = 7;           // handle tube radius
h_handle = 60;          // handle height
w_handle = 50;          // handle width
r_switch = 4;           // on/off switch radius
h_switch = 6;           // switch protrusion
r_led = 2;              // indicator LED radius

// ---------------- Modules ----------------
module kettle_body() {
    difference() {
        // outer skin
        cylinder(h = h_body, r1 = r_body, r2 = r_body - 5);
        // hollow cavity
        translate([0, 0, wall])
            cylinder(h = h_body - wall, r1 = r_body - wall, r2 = r_body - wall - 5);
    }
}

module lid() {
    difference() {
        union() {
            // lid disc
            cylinder(h = h_lid, r = r_lid);
            // knob
            translate([0, 0, h_lid])
                sphere(d = 12);
        }
        // recess knob base for printability
        translate([0, 0, h_lid - 1])
            sphere(d = 10);
    }
}

module spout() {
    translate([r_body - 10, 0, h_body - 10])
        rotate([0, -25, 0])
            hull() {
                cylinder(h = 5, r = r_spout);
                translate([l_spout, 0, h_spout - (h_body - 10)])
                    cylinder(h = 3, r = r_spout - 1);
            }
}

module handle() {
    translate([-r_body - 2, 0, 0])
        rotate([90, 0, 0])
            difference() {
                // outer tube
                cylinder(h = w_handle, r = r_handle);
                // inner hole
                cylinder(h = w_handle + 1, r = r_handle - wall);
            }
}

module base() {
    difference() {
        cylinder(h = h_base, r = r_base);
        translate([0, 0, wall])
            cylinder(h = h_base, r = r_base - wall);
    }
}

module switch() {
    translate([r_base - 15, 0, h_base + h_switch/2])
        rotate([90, 0, 0])
            cylinder(h = h_switch, r = r_switch);
}

module led_indicator() {
    translate([r_base - 8, 0, h_base + 0.5])
        sphere(r = r_led);
}

// ---------------- Assembly ----------------
union() {
    color("silver") base();
    color("gainsboro") kettle_body();
    color("white") translate([0, 0, h_body]) lid();
    color("gainsboro") spout();
    color("black") handle();
    color("red") switch();
    color("lime") led_indicator();
}

