        // Hollow interior
        translate([0, 0, 2])
        cylinder(h=body_h-2, r1=body_w/2-2, r2=body_w/2-2, $fn=32);

        // Spout hole
        translate([body_w/2-2, 0, spout_h])
        rotate([0, 0, 30])
