    difference() {
        // Main body block
        cube([body_w, body_d, body_h], center = true);
        // Hollow cavity
        translate([0, 0, -wall/2])
            cube([body_w - 2*wall, body_d - 2*wall, body_h], center = true);
    }
}

// Lid with hinge pins
module lid() {
    // Hinge cylinders
    for (x = [-lid_w/2 + hinge_l, lid_w/2 - hinge_l]) {
        translate([x, lid_d/2, 0])
            rotate([90, 0, 0])
                cylinder(r = hinge_r, h = hinge_l, center = true);
    }

    // Lid plate
    translate([0, 0, lid_h/2])
        difference() {
            cube([lid_w, lid_d, lid_h], center = true);
            translate([0, 0, wall/2])
                cube([lid_w - 2*wall, lid_d - 2*wall, lid_h], center = true);
        }
}

// Handle attached to lid
module handle() {
    translate([0, -lid_d/2 - handle_len/2, lid_h])
        rotate([90, 0, 0])
            cylinder(r = handle_grip_r, h = handle_len, center = true);

    // Brackets
    for (x = [-lid_w/4, lid_w/4])
        translate([x, -lid_d/2, lid_h/2])
            cube([wall*2, wall*2, lid_h], center = true);
}

