module reservoir() {
    translate([0, 0, -body_h/2 + reservoir_h/2])
        difference() {
            cube([reservoir_w, reservoir_d, reservoir_h], center = true);
            translate([0, 0, wall])
                cube([reservoir_w - 2*wall, reservoir_d - 2*wall, reservoir_h - wall], center = true);
