// IceNest – a compact, 3D-printable ice-cube tray / dispenser
// Overall footprint 100 x 80 x 110 mm (fits most home FDM printers)

$fn = 36;

// ---------- PARAMETERS ----------
wall = 2;           // uniform 2 mm shell thickness
base_h = 15;        // pedestal height
body_w = 80;        // main body width
body_d = 60;        // main body depth
body_h = 70;        // main body height
lid_h  = 20;        // lid height
button_r = 6;       // eject button radius
cube_cnt = 6;       // number of cube columns
cube_w   = 10;      // single ice-cube width
cube_gap = 3;       // gap between cubes
handle_len = 40;    // lid handle length
handle_d   = 8;

// ---------- MAIN ASSEMBLY ----------
difference() {
    union() {
        base();
        body();
        lid();
    }
    // hollow the body and base
    translate([0, 0, base_h + wall])
    cube([body_w - 2*wall, body_d - 2*wall, body_h - wall], center = true);
    translate([0, 0, wall])
    cube([body_w - 2*wall, body_d - 2*wall, base_h - wall], center = true);
}

// ---------- MODULES ----------

module base() {
    // pedestal with rounded edges
    difference() {
        minkowski() {
            cube([body_w, body_d, base_h], center = true);
            sphere(r = wall);
        }
        translate([0, 0, -base_h/2 - 1])
        cube([body_w - 2*wall, body_d - 2*wall, base_h + 2], center = true);
    }
}

module body() {
    // main container block
    translate([0, 0, base_h + body_h/2])
    difference() {
        cube([body_w, body_d, body_h], center = true);
        // ice-cube cavities
        for (i = [0 : cube_cnt - 1]) {
            translate([-body_w/2 + wall + cube_w/2 + i*(cube_w + cube_gap),
                       0,
                       base_h + body_h - wall - cube_w/2])
            cube([cube_w, body_d - 2*wall, cube_w], center = true);
        }
        // eject button hole
        translate([body_w/2 - wall - button_r - 3,
                   0,
                   base_h + body_h/2])
        rotate([90, 0, 0])
        cylinder(r = button_r, h = wall + 2, center = true);
    }
    // eject button
    translate([body_w/2 - wall - button_r - 3,
               0,
               base_h + body_h/2 + button_r])
    rotate([90, 0, 0])
    cylinder(r1 = button_r, r2 = button_r + 1, h = 8, center = true);
}

module lid() {
    // hinged lid
    translate([0, 0, base_h + body_h + lid_h/2])
    difference() {
        cube([body_w, body_d, lid_h], center = true);
        // inner clearance
        translate([0, 0, -1])
        cube([body_w - 2*wall, body_d - 2*wall, lid_h + 2], center = true);
    }
    // lid handle
    translate([0, body_d/2 + handle_len/2 + wall, base_h + body_h + lid_h/2])
    rotate([90, 0, 0])
    cylinder(h = handle_len, r = handle_d/2, center = true);
}

