// Sub-Zero style kitchen bin
// Overall dimensions: 100mm x 80mm x 120mm

$fn = 32;

// Main parameters
bin_width = 100;
bin_depth = 80;
bin_height = 120;
wall_thickness = 2;
lid_thickness = 3;

// Main body module
module bin_body() {
    // Outer shell
    difference() {
        cube([bin_width, bin_depth, bin_height]);

        // Hollow interior
        translate([wall_thickness, wall_thickness, 0])
            cube([bin_width - 2*wall_thickness, 
                  bin_depth - 2*wall_thickness, 
                  bin_height - wall_thickness]);
    }
}

// Stainless steel lid
module bin_lid() {
    // Main lid panel
    difference() {
        translate([0, 0, bin_height])
            cube([bin_width, bin_depth, lid_thickness]);

        // Rounded edges on top
        translate([-1, -1, bin_height + lid_thickness])
            cube([bin_width + 2, bin_depth + 2, 2]);
    }

    // Hinge cylinders
    translate([5, 0, bin_height])
        rotate([90, 0, 0])
            cylinder(h=5, r=3);

    translate([bin_width - 5, 0, bin_height])
        rotate([90, 0, 0])
            cylinder(h=5, r=3);
}

// Pedal mechanism
module pedal_system() {
    // Pedal bar
    translate([bin_width/2 - 15, -5, wall_thickness])
        cube([30, 10, 5]);

    // Pedal plate
    translate([bin_width/2 - 20, -15, 0])
        cube([40, 10, wall_thickness]);

    // Support brackets
    for(i = [0, 1]) {
        translate([20 + i*(bin_width - 40), 0, 0]) {
            cube([5, 10, 15]);
        }
    }
}

// Handle grip on lid
module lid_handle() {
    translate([bin_width/2 - 15, bin_depth - 5, bin_height + lid_thickness])
        cube([30, 5, 8]);

    // Rounded edges
    translate([bin_width/2 - 15, bin_depth - 3, bin_height + lid_thickness + 8])
        cube([30, 2, 2]);
}

// Ventilation holes
module ventilation() {
    for(i = [0:3]) {
        translate([10 + i*20, bin_depth/2, bin_height/2])
            rotate([0, 90, 0])
                cylinder(h=2, r=3);
    }
}

// Brand label area
module brand_label() {
    translate([bin_width/2 - 20, bin_depth - 1, bin_height/2])
        cube([40, 2, 15]);
}

// Assembly
union() {
    bin_body();
    bin_lid();
    pedal_system();
    lid_handle();
    ventilation();
    brand_label();
}

