    translate([0, 0, height/2 + wall/2]) {
        // Lid body
        difference() {
            cube([width-wall*2, depth-wall*2, wall*2], center = true);
            translate([0, 0, wall])
                cube([width-wall*4, depth-wall*4, wall*2], center = true);
        }

        // Handle
        translate([0, 0, wall*1.5])
            difference() {
                cube([width/3, wall*2, wall*2], center = true);
                translate([0, 0, wall/2])
                    cube([width/3-wall, wall*2-wall*2, wall], center = true);
            }
    }
}

