    translate([width/2 + wall/2, 0, height/3]) {
        rotate([0, 90, 0]) {
            // Panel backing
            cube([height/3, depth/2, wall], center = true);

            // Buttons
            for(i = [0:2]) {
                translate([0, -depth/6 + i*depth/6, wall/2 + 0.1])
                    cylinder(h=wall/2, r=wall/1.5, $fn=16);
