    translate([width/3-5, 0, height/2+height/4+height/6])
    rotate([0, 90, 0])
    difference() {
        cylinder(h=height/6, r=4, $fn=16);
        cylinder(h=height/6, r=2, $fn=16);
    }

    // Lid
    translate([0, 0, height/2+height/4+height/3+2])
    difference() {
        cylinder(h=4, r=width/3-2, $fn=32);
        translate([0, 0, -1])
            cylinder(h=6, r=width/3-4, $fn=32);
    }
}

// Power cord detail
module power_cord() {
    translate([-width/2, depth/2, 0])
    rotate([90, 0, 0])
    cylinder(h=20, r=2, $fn=8);
}

// Assembly
union() {
    base_frame();
    translate([0, 0, 4])
        main_body();
    control_panel();
    motor_housing();
    blender_jar();
    power_cord();
}

