// WarmLocker Kitchen Appliance - Realistic Style, Medium Complexity
// Overall dimensions: 80x90x110mm (WxDxH)

$fn = 50;
wall = 2;

module roundedCube(size, radius) {
    // Create a cube with rounded edges
    translate([radius, radius, 0])
    minkowski() {
        cube([size.x - 2*radius, size.y - 2*radius, size.z]);
        sphere(r = radius);
    }
}

module mainBody() {
    // Main housing - 80x90x100mm
    difference() {
        roundedCube([80, 90, 100], 3);
        // Hollow out interior
        translate([wall, wall, wall])
        roundedCube([80 - 2*wall, 90 - 2*wall, 100 - wall], 2);
    }
}

