    translate([bowl_diameter/2 + 15, 0, base_height + motor_housing_height + bowl_height/2])
        rotate([0, 90, 0])
            difference() {
                cylinder(h=30, r=10, center=true);
                translate([0, 0, 5])
                    cylinder(h=30, r=6, center=true);
            }
}

// Lid with feed tube
module lid() {
    difference() {
        // Main lid
        translate([0, 0, base_height + motor_housing_height + bowl_height + lid_height/2])
            cylinder(h=lid_height, r=lid_diameter/2, center=true);

        // Inner recess
        translate([0, 0, base_height + motor_housing_height + bowl_height + lid_height/2 - 2])
            cylinder(h=lid_height, r=lid_diameter/2 - 5, center=true);

        // Feed tube hole
