// Overall footprint ~120 x 120 x 70 mm

// --- PARAMETERS --------------------------------
wall = 2;                // printable wall thickness [mm]
dia = 110;               // burner top diameter [mm]
h_base = 20;             // base height [mm]
h_body = 30;             // burner body height [mm]
h_bracket = 8;           // pot-support bracket height [mm]
n_brackets = 4;          // number of pot supports
bracket_len = 35;        // pot support length [mm]
bracket_w = 10;          // pot support width [mm]
knob_d = 20;             // control knob diameter [mm]
knob_h = 15;             // control knob height [mm]
igniter_d = 8;           // igniter pin diameter [mm]
igniter_h = 10;          // igniter pin height [mm]

// --- BASE --------------------------------------
module base() {
    difference() {
        // main cylindrical base
        cylinder(h=h_base, d=dia+6, $fn=64);
        // lightening cavity
        translate([0,0,wall])
            cylinder(h=h_base-wall+1, d=dia+6-wall*2, $fn=64);
    }
}

// --- BODY / RING -------------------------------
module body() {
    translate([0,0,h_base]) {
        difference() {
            // outer cylinder
            cylinder(h=h_body, d=dia, $fn=64);
            // inner cavity
            translate([0,0,wall])
                cylinder(h=h_body+1, d=dia-wall*2, $fn=64);
        }
    }
}

// --- POT-SUPPORT BRACKETS ----------------------
module bracket() {
    difference() {
        // main bracket
        cube([bracket_len, bracket_w, h_bracket], center=true);
        // chamfer underside for printability
        translate([0,0,-h_bracket/2])
            cube([bracket_len-wall, bracket_w+1, wall], center=true);
    }
}

module brackets() {
