    translate([base_r-wall*0.5, 0, base_h*0.6])
        rotate([0, 90, 0])
            difference(){
                // main grip
                cylinder(h=handle_len, r=handle_d/2, $fn=32);
                // finger groove hole
                for(i=[-1,1])
                    translate([i*handle_len/4, 0, 0])
                        rotate([0, 90, 0])
                            cylinder(h=handle_d*0.8, r=handle_d*0.3, $fn=24);
            }
