        // inner hollow
        translate([0, 0, base_h+wall*1.5+base_r*0.45])
            scale([1, 1, 0.5])
                sphere(r=base_r-wall, $fn=64);
        // steam vent
        translate([base_r*0.6, 0, base_h+wall*1.5+base_r*0.45])
            cylinder(h=base_r, r=knob_r*0.4, $fn=24);
    }
}

