// Amphisbaena - A two-headed serpent from mythology
// Size: ~70mm long, designed for 3D printing

// Main parameters
body_radius = 6;
body_length = 60;
head_radius = 8;
segment_count = 8;
scale_factor = 0.85;

// Color definitions
$fn = 32;

// Helper module for body segments
module body_segment(pos, size, rot) {
    translate(pos)
    rotate([0, rot, 0])
    scale([size, size, size])
    color("darkgreen")
    difference() {
        sphere(r = body_radius);
        // Create scale texture
        for(i = [0:5]) {
            rotate([0, i * 60, 0])
            translate([0, -body_radius - 0.5, 0])
            cube([body_radius * 2, 1, body_radius * 2], center = true);
        }
    }
}

// Head module with two faces
module head(pos, direction) {
    translate(pos)
    rotate([0, direction, 0])
    union() {
        // Main head sphere
        color("forestgreen")
        sphere(r = head_radius);

        // Snout
        translate([head_radius * 0.8, 0, 0])
        color("forestgreen")
        scale([1.5, 0.7, 0.7])
        sphere(r = head_radius * 0.7);

        // Eyes
        color("red")
        translate([head_radius * 1.2, head_radius * 0.4, head_radius * 0.4])
        sphere(r = 1.5);
        translate([head_radius * 1.2, -head_radius * 0.4, head_radius * 0.4])
        sphere(r = 1.5);

        // Nostrils
        color("black")
        translate([head_radius * 1.4, head_radius * 0.3, 0])
        sphere(r = 0.8);
        translate([head_radius * 1.4, -head_radius * 0.3, 0])
        sphere(r = 0.8);

        // Horns
        color("darkgray")
        translate([0, 0, head_radius * 0.8])
        rotate([0, 30, 0])
        cylinder(h = 5, r1 = 1.5, r2 = 0.5);
        translate([0, 0, -head_radius * 0.8])
        rotate([0, -30, 0])
        cylinder(h = 5, r1 = 1.5, r2 = 0.5);
    }
}

// Create the serpent body
union() {
    // First head
    head([body_length/2, 0, 0], 0);

    // Body segments
    for(i = [1:segment_count]) {
        segment_pos = body_length/2 - (i * (body_length/segment_count));
        segment_size = pow(scale_factor, i);
        body_segment([segment_pos, 0, 0], segment_size, i * 5);
    }

    // Second head (reversed)
    head([-body_length/2, 0, 0], 180);

    // Connecting body cylinder
    color("darkgreen")
    difference() {
        cylinder(h = body_length, r = body_radius, center = true);
        // Hollow center for material saving
        cylinder(h = body_length + 1, r = body_radius - 3, center = true);
    }

    // Underbelly scales
    for(i = [0:15]) {
        angle = i * 22.5;
        color("yellowgreen")
        translate([body_length/2 - (i * 4), 0, -body_radius + 0.5])
        rotate([0, angle, 0])
        scale([1, 0.5, 0.3])
        sphere(r = 2);
    }
}

