color_beak = [0.8, 0.7, 0.3];

// Main module
module quetzalcoatl() {
    union() {
        // Serpent body
        color(color_scales)
        translate([0, 0, 5])
        rotate([90, 0, 0])
        scale([1, 1, 8])
        cylinder(h = 40, r1 = 8, r2 = 3);

        // Head
        color(color_scales)
        translate([0, 0, 45])
        sphere(10);

        // Beak/snout
        color(color_beak)
        translate([0, 0, 53])
        rotate([0, 0, 0])
        scale([0.7, 0.4, 3])
        cylinder(h = 8, r = 5);

        // Eyes
        color(color_eyes)
        translate([5, 5, 48])
        sphere(3);
        translate([-5, 5, 48])
        sphere(3);

        // Feathered crest
        color(color_feathers)
        for(i = [0:15:165]) {
            rotate([0, i, 0])
            translate([0, 0, 50])
            rotate([90, 0, 0])
            scale([0.2, 2, 0.1])
            cylinder(h = 12, r = 1);
        }

        // Wings
        color(color_feathers)
        translate([15, 0, 25])
        rotate([0, 0, 30])
        scale([1, 3, 0.3])
        sphere(8);

        translate([-15, 0, 25])
        rotate([0, 0, -30])
        scale([1, 3, 0.3])
        sphere(8);

        // Wing feathers
        color(color_feathers)
        for(i = [0:20:140]) {
            rotate([0, 0, 30])
            translate([15 + cos(i) * 8, sin(i) * 8, 25])
            rotate([0, 0, -i])
            scale([0.5, 1.5, 0.1])
            cylinder(h = 6, r = 1);
        }

        for(i = [0:20:140]) {
            rotate([0, 0, -30])
            translate([-15 + cos(i) * -8, sin(i) * 8, 25])
            rotate([0, 0, i])
            scale([0.5, 1.5, 0.1])
            cylinder(h = 6, r = 1);
        }

        // Tail feathers
        color(color_feathers)
        for(i = [0:20:160]) {
            rotate([0, i, 0])
            translate([0, 0, 0])
            rotate([90, 0, 0])
            scale([0.3, 3, 0.1])
            cylinder(h = 15, r = 1.5);
        }

        // Body scales pattern
        color(color_scales)
        for(i = [5:5:35]) {
            rotate([90, 0, 0])
            translate([0, 0, i])
            difference() {
                cylinder(h = 2, r = 8 - i/10);
                cylinder(h = 2.1, r = 7 - i/10);
            }
        }

        // Magical aura around head
        color([0.2, 0.8, 0.3, 0.3])
        translate([0, 0, 45])
        sphere(15);
    }
}

// Scale to printable size
scale([0.8, 0.8, 0.8])
