// Crystaldrake - Medium complexity realistic style
// A crystalline dragon with gem-like wings and tail

$fn = 32;

// Main scale factor
scale_factor = 0.5;

// Colors
crystal_color = "LightBlue";
horn_color = "Silver";
eye_color = "Red";

// Head module
module head() {
    color(crystal_color) {
        // Main head shape
        translate([0, 0, 8]) 
            scale([1.2, 0.8, 1]) 
                sphere(6);

        // Snout
        translate([6, 0, 7]) 
            scale([1.5, 0.7, 0.8]) 
                sphere(4);

        // Horns
        color(horn_color) {
            translate([0, -5, 12]) 
                rotate([0, 15, 0]) 
                    cylinder(h=8, r1=2, r2=0.5);
            translate([0, 5, 12]) 
                rotate([0, -15, 0]) 
                    cylinder(h=8, r1=2, r2=0.5);
        }

        // Eyes
        color(eye_color) {
            translate([4, -2.5, 10]) 
                sphere(1.2);
            translate([4, 2.5, 10]) 
                sphere(1.2);
        }
    }
}

// Neck module
module neck() {
    color(crystal_color) {
        for(i = [0:4]) {
            translate([-i*2, 0, 8-i*1.5]) 
                scale([1.1 - i*0.1, 1.1 - i*0.1, 1.5]) 
                    sphere(3);
        }
    }
}

// Body module
module body() {
    color(crystal_color) {
        // Main body
        translate([-12, 0, 5]) 
            scale([2.5, 1.5, 1.2]) 
                sphere(8);

        // Chest plates
        translate([-15, 0, 3]) 
            scale([1.5, 1.2, 0.5]) 
                sphere(5);
    }
}

// Wing module
module wing() {
    color(crystal_color) {
        // Wing membrane
        translate([-10, 12, 8]) 
            rotate([0, 0, 20]) 
                scale([3, 0.1, 2]) 
                    sphere(5);

        // Wing support
        translate([-5, 18, 12]) 
            rotate([0, 0, 45]) 
                cylinder(h=15, r1=1.5, r2=0.5);

        // Crystal formations
        for(i = [0:2]) {
            translate([-8 - i*3, 15 + i*2, 10 - i]) 
                rotate([0, 0, i*20]) 
                    scale([0.5, 0.5, 1.5]) 
                        sphere(2);
        }
    }
}

// Tail module
module tail() {
    color(crystal_color) {
        // Main tail segments
        for(i = [0:6]) {
            translate([-20 - i*4, 0, 5 - i*0.5]) 
                scale([1 - i*0.12, 1 - i*0.12, 1 - i*0.1]) 
                    sphere(4 - i*0.5);
        }

        // Crystal spikes
        for(i = [0:3]) {
            translate([-25 - i*6, 0, 6 - i]) 
                rotate([0, 20 + i*10, 0]) 
                    cylinder(h=3, r1=1.5, r2=0);
        }
    }
}

// Leg module
module leg(side) {
    color(crystal_color) {
        // Upper leg
        translate([-12, side*6, 0]) 
            rotate([0, 15, 0]) 
                cylinder(h=8, r1=2.5, r2=2);

        // Lower leg
        translate([-18, side*7, -2]) 
            rotate([0, 15, 0]) 
                cylinder(h=7, r1=2, r2=1.5);

        // Foot
        translate([-23, side*8, -5]) 
            scale([1.5, 0.8, 0.5]) 
                sphere(2);
    }
}

// Main assembly
scale([scale_factor, scale_factor, scale_factor]) {
    head();
    neck();
    body();

    // Wings - two sides
    wing();
    mirror([0, 1, 0]) wing();

    tail();

    // Legs
    leg(1);
    leg(-1);
}

