// Mistwyrm - Medium complexity, realistic style
// A serpentine dragon that weaves through mist, with ethereal wings and flowing whiskers

module body_segment(r, h, t=2) {
    // Scaled cylindrical segment with taper
    cylinder(r1=r, r2=r*0.9, h=h, $fn=32);
}

module mist_scales(base_r) {
    // Overlapping scale pattern along body
    for(i=[0:20:340]) {
        rotate([0, 0, i]) 
        translate([base_r*0.9, 0, 0]) 
        rotate([90, 0, 0]) 
        scale([1, 0.7, 0.3]) 
        sphere(r=base_r*0.2, $fn=8);
    }
}

module wing_segment(span, chord) {
    // Membranous wing with subtle curve
    difference() {
        hull() {
            translate([0, 0, 0]) sphere(r=1, $fn=8);
            translate([span, chord*0.3, 0]) sphere(r=1, $fn=8);
            translate([span*0.8, chord, 0]) sphere(r=1, $fn=8);
            translate([span*0.2, chord*0.9, 0]) sphere(r=1, $fn=8);
        }

        // Wing holes for ethereal effect
        for(i=[span*0.2:span*0.2:span*0.8]) {
            translate([i, chord*0.5, -1]) 
            scale([0.5, 0.3, 2]) 
            sphere(r=2, $fn=6);
        }
    }
}

module whisker(len, angle) {
    // Flowing mystical whiskers
    rotate([0, angle, 0]) {
        color("silver", 0.6)
        cylinder(r1=0.5, r2=0.1, h=len, $fn=8);
    }
}

