// Wings folded, coiled pose for 3D printing without supports

$fn = 36;  // Smooth curves for realism

// Main body assembly
module rubywyrm() {
    color("firebrick") {
        union() {
            // Central body coil
            body_coil();

            // Head with horns
            translate([0, 30, 25]) rotate([0, 0, 45]) head();

            // Wings folded against body
            translate([-15, 10, 15]) rotate([0, 0, -30]) wing_left();
            translate([15, 10, 15]) mirror([1,0,0]) wing_left();

            // Tail wrapping around
            tail();

            // Legs tucked under body
            legs();

            // Ruby gem embedded in chest
            translate([0, 5, 18]) ruby_gem();
        }
    }
}

// Coiled serpentine body
module body_coil() {
    // Main torso segment
    translate([0, 0, 12]) scale([1.2, 1, 1.3]) sphere(12);

    // Body segments forming coil
    for(i = [0:30:330]) {
        rotate([0, 0, i]) 
        translate([20*sin(i/3), 20*cos(i/3), 12 + 3*sin(i*2)]) 
        scale([0.9, 0.9, 1]) sphere(10 - i/40);
    }
}

// Detailed dragon head
module head() {
    union() {
        // Snout
        translate([0, 12, 0]) scale([0.8, 1.5, 0.7]) sphere(6);

        // Main head
        sphere(8);

        // Horns - two pairs
        translate([0, 8, 6]) rotate([15, 0, 0]) cylinder(10, 2, 0.5);
        translate([4, 8, 5]) rotate([25, 0, -20]) cylinder(8, 1.5, 0.5);
        translate([-4, 8, 5]) rotate([25, 0, 20]) cylinder(8, 1.5, 0.5);

        // Eyes
        translate([3, 6, 2]) color("gold") sphere(1.5);
        translate([-3, 6, 2]) color("gold") sphere(1.5);

        // Nostrils
        translate([2, 10, -1]) rotate([45, 0, 0]) cylinder(2, 1, 0.5);
        translate([-2, 10, -1]) rotate([45, 0, 0]) cylinder(2, 1, 0.5);
    }
}

// Folded wing design for supportless printing
module wing_left() {
    difference() {
        // Main wing membrane
        translate([0, 0, 0]) 
        rotate([0, 90, 0]) 
        scale([1, 2, 0.1]) 
