gold    = [0.85, 0.65, 0.15];
obsidian= [0.05, 0.05, 0.05];

// ---------- Main assembly ----------
module emeralddrake() {
    union() {
        color(emerald) body();
        color(emerald) head();
        color(emerald) tail();
        color(emerald) wings();
        color(gold)    horns();
        color(obsidian) eyes();
        color(emerald) scales();
    }
}

// ---------- Body ----------
module body() {
    // Central torso
    translate([0,0,12])
        scale([1,0.7,1.2])
        sphere(12);

    // Neck
    translate([0,-15,22])
        rotate([45,0,0])
        cylinder(h=20, r1=6, r2=8, $fn=16);

    // Hind legs
    for(side=[-1,1])
        translate([side*8,-8,5])
            rotate([0,side*10,0])
            leg();

    // Front legs
    for(side=[-1,1])
        translate([side*7,-3,5])
            rotate([0,side*10,0])
            leg();
}

// ---------- Leg ----------
module leg() {
    union() {
        // Thigh
        translate([0,0,2])
            rotate([90,0,0])
            cylinder(h=10, r1=4, r2=3, $fn=8);

        // Shin
        translate([0,0,-7])
            rotate([90,0,0])
            cylinder(h=8, r1=3, r2=2.5, $fn=8);

        // Foot
        translate([0,0,-14])
            rotate([90,0,0])
            cylinder(h=4, r1=3, r2=4, $fn=8);
    }
}

// ---------- Head ----------
module head() {
    translate([0,-28,28])
        rotate([30,0,0])
        difference() {
            scale([1.2,1.4,1])
            sphere(10);

            // Snout narrowing
            translate([0,-12,0])
                scale([0.5,1,1])
                cube(20, center=true);

            // Nostrils
            for(x=[-2,2])
                translate([x,-14,0])
                    rotate([90,0,0])
                    cylinder(h=3, r=1, $fn=8);
        }
}

// ---------- Horns ----------
module horns() {
    // Main curved horns
    for(side=[-1,1])
        translate([side*6,-25,35])
            rotate([0,side*20,side*30])
            scale([1,1,1.5])
            difference() {
                cylinder(h=12, r1=2, r2=0.5, $fn=8);
                translate([0,0,-1])
                    cylinder(h=14, r1=1.8, r2=0.4, $fn=8);
            }
}

// ---------- Eyes ----------
module eyes() {
    for(side=[-1,1])
        translate([side*4,-22,30])
            sphere(1.5);
}

// ---------- Wings ----------
module wings() {
    for(side=[-1,1]) {
        translate([side*12,0,25])
            rotate([0,0,side*20])
            rotate([0,90,0])
            wing_arm(side);
    }
}

module wing_arm(sign) {
    // Arm
    translate([0,0,0])
        rotate([0,sign*20,0])
        cylinder(h=20, r1=2, r2=1.5, $fn=8);

    // Membrane segments (no overhangs)
    for(i=[1:3]) {
        hull() {
            translate([0,0,i*5])
                sphere(1.5);
            translate([sign*15,0,i*5])
                sphere(1);
        }
    }

    // Connect membrane edges
    hull() {
        translate([0,0,5])
            sphere(1.5);
        translate([sign*15,0,15])
            sphere(1);
    }
}

// ---------- Tail ----------
module tail() {
    for(i=[0:4]) {
        translate([0,10+i*8,12-i*2])
            rotate([0,0,i*10])
            scale([1-i*0.15,1-i*0.15,1-i*0.1])
